LET $count := COALESCE($count, 1); 

LET $result := HASH_GET([Temp], "Question Data"); 
LET $columns := HASH_GET([Temp], "Question Columns"); 

LET $exclusionList := ARRAY("RecordID"); 

LET $columns := 
ARRAY_WHERE(
  $columns, $(c, column) 
  ARRAY_INDEXOF($exclusionList, $column) = NULL
);

LET $numberOfRecordsToRaise := ARRAY_LENGTH(HASH_KEYS($result));

LET $recordToCreate := ARRAY_INDEX(HASH_KEYS($result), $count);

LET $recordToCreateData := HASH_GET($result, $recordToCreate); 

LET $mapped :=  
ARRAY_SELECT(
  $columns, $(c, column) 
  ARRAY(
    $column, 
    ARRAY_SELECTMANY(
      $recordToCreateData, $(i, item)
	  HASH_GET($item, $column)
    )
  )
);

LET $dataStructure := 
INDEXES_TO_KEYS(
  ARRAY_INDEX(ARRAY_TRANSPOSE($mapped), 2), 
  ARRAY_INDEX(ARRAY_TRANSPOSE($mapped), 1)
);


LET $questionSortId := 
  ARRAY_FIRST(ARRAY_FIRST(DB_REPORT(
     "MAX([Question Sort ID])", 
     "Standard Question", 
     "[LineNumber] = 1" & 
     " AND [Standard Questionnaire RecordID] =  " & DB_ENCODECONSTANT([RecordID]) & 
     " AND [Question Category UUID] =  " & DB_ENCODECONSTANT([Category Filter]) &
     " AND [Question Group] IS NULL"
  ))) + 1; 


LET $groupSortId :=
     ARRAY_FIRST(ARRAY_FIRST(DB_REPORT(
       "MAX([Group Sort ID])", 
       "Standard Question", 
       "[LineNumber] = 1" & 
       " AND [Standard Questionnaire RecordID] =  " & DB_ENCODECONSTANT([RecordID]) & 
       " AND [Question Category UUID] =  " & DB_ENCODECONSTANT([Category Filter]) & 
	   " AND [Question GUID] = " & DB_ENCODECONSTANT(HASH_GET(HASH_GET([Temp], "Criteria Data"), "Criteria UUID"))
     )));

RETURN 
HASH_MERGE(
  $dataStructure, 
  HASH("Linked Entity", ARRAY(ARRAY([EFormType], [RecordID]))),
  HASH("Linked Entity EFormType", [EFormType]),
  HASH("Linked Entity RecordID", [RecordID]),
  //data from standard questionnaire
  HASH(
	"Question Category UUID", [Category Filter],
	"Standard Questionnaire RecordID", [RecordID],
	"Question Category", [Question Category]#(ARRAY_INDEXOF(EACH [Question Category UUID], [Category Filter])), 
	"Question Type", "Unscored", 
	"Question Group", HASH_GET(HASH_GET([Temp], "Criteria Data"), "Criteria UUID"),
	"Group Name", HASH_GET(HASH_GET([Temp], "Criteria Data"), "Criteria Name"),
	"Question Sort ID", $questionSortId + $count, 
	"Group Sort ID", $groupSortId
  )
)

FieldEntry.Formula:
/* $eventargs: Hash of - RecordID - Unique Name - Criteria Name - Criteria UUID */
LET $questionParent := ARRAY( "_EvaluationStandardCriteria", HASH_GET($eventargs, "RecordID") );
LET $questionColumns := DB_GETCOLUMNS("Standard Question");
LET $exclusionList := ARRAY( "Assignee", "DateClosed", "DateCreated", "DateDue", "DateLastUpdated", "EFormType", "LineNumber", "Linked Entity EFormType", "Linked Entity RecordID", "Linked Entity", "Originator", "Parent Chain", "Priority", "Range Preview", "Temp", "Sender", "Status", "Was Rejected", "Unique Reference" );
LET $questionColumns := ARRAY_WHERE( $questionColumns, $(c, column) ARRAY_INDEXOF($exclusionList, $column) = NULL );
LET $questionLookupData := DB_LOOKUPALL( $questionColumns, "Standard Question", "[Status] <> 'Deleted'", HASH("parent", $questionParent) );
LET $hashed := ARRAY_SELECT( $questionLookupData, $(d, data) INDEXES_TO_KEYS($data, $questionColumns) );
LET $questionData := ARRAY_TO_HASH_GROUPED( $hashed, $(i, item) HASH_GET($item, "RecordID"), $(i, item) $item );
//---end question data
RETURN HASH( "Criteria Data", $eventargs, "Question Data", $questionData, "Question Columns", $questionColumns )

LET $importLine := ARRAY_INDEXOF(EACH [Import - Criteria Set Selected], TRUE); 

LET $recordID := 
HASH_GET(
  [Import - Criteria Set Data]#$importLine, 
  "RecordID"
);

LET $criteriaUUIDs :=  
DB_LOOKUPALL(
  "Criteria UUID",
  "_EvaluationStandardCriteriaSet", 
  "[RecordID] = " & $recordID,
  HASH("sort", "[LineNumber] ASC") 
);

LET $filterString := 
ARRAY_JOIN(
	ARRAY_SELECT(
	$criteriaUUIDs, $(i, item) 
	"[Criteria UUID] = " & DB_ENCODECONSTANT($item)
	), " OR "
); 

LET $data :=  
DB_LOOKUPALL(
  ARRAY(
    "RecordID", 
	"Unique Name", 
	"Criteria Name", 
	"Criteria UUID"
  ),
  "_EvaluationStandardCriteria", 
  "[LineNumber] = 1" & " AND (" & $filterString & ")",
  HASH("sort", "[LineNumber] ASC") 
);

RETURN 
ARRAY_SELECT(
  $data, $(i, item)
  INDEXES_TO_KEYS($item, ARRAY("RecordID", "Unique Name", "Criteria Name", "Criteria UUID")
  )
)

FormulaDrivenChangeFieldValues.FieldValuesFormula:
LET $columns:=
ARRAY(
  "Question Category", 
  "Questions in Category", 
  "Category Max Score",
  "Question Category UUID"  
);


LET $buildArray:= $(columnName, line, direction)(
	LET $array	:= CURRENT_EACHVALUE($columnName);
	RETURN
		IF( $direction = "up",
			ARRAY_CONCAT(
			  //	$direction,
				ARRAY_TAKE($array, $line-2), //$before,
				CURRENT_VALUE($columnName, $line), 
			  	CURRENT_VALUE($columnName, $line-1),
				ARRAY_SKIP($array, $line) //$after
			),
			$direction = "down",
			ARRAY_CONCAT(
			  //	$direction,
				ARRAY_TAKE($array, $line-1), //$before,
				CURRENT_VALUE($columnName, $line+1), 
			  	CURRENT_VALUE($columnName, $line),
				ARRAY_SKIP($array, $line+1) //$after
			),
			$array
		  )
);
RETURN 
ARRAY_TO_HASH(
  $columns, 
  $(i, columnName) $columnName, //key is column name
  $(i, columnName) CALL( $buildArray, $columnName, HASH_GET($eventargs, "line"), HASH_GET($eventargs, "direction"))
  )

FormulaDrivenRaiseLinkedEForm.FieldValuesFormula:
LET $recordID := [RecordID];
LET $title := "Digital Submission - Questionnaire Preview";
LET $evaluator := ROLE(EBMS_GET_CURRENT_USER());
LET $type := "Digital Submission";
LET $customRecordIDFilter := HASH_GET($eventargs, "Questionnaire RecordIDs Custom Filter");

LET $questionnaireRecordIDs :=
DB_LOOKUPALL(
  "RecordID",
  "Questionnaire", 
  $customRecordIDFilter & " AND [LineNumber] FAST_EQUAL_TO 1"
);

LET $questionnaireHeaderData :=
(
  LET $columns := 
  ARRAY(
  	"RecordID",
    "Status",
  	"Participant",
    "Participant Name",
  	"Assignee", 
  	"Evaluated Party", 
  	"Assessor Name", 
    "Assessor",
  	"Assessment Started",
  	"Assessment Complete",
  	"Completion - Allow Resubmit",
  	"Locked"
  );


  LET $lookup := 
  DB_LOOKUPALL(
    $columns, 
    "Questionnaire", 
    "[LineNumber] FAST_EQUAL_TO 1 AND [Status] <> 'Deleted' AND [RecordID] IN " & DB_ENCODECONSTANT($questionnaireRecordIDs)
  );

  RETURN 
  ARRAY_TO_HASH(
  	$lookup, $(i,item) ARRAY_INDEX($item, 1), //RecordID
  	$(i,item) INDEXES_TO_KEYS($item, $columns)
  )
);

LET $questionnaireStatus :=
(
  LET $allStatus:= 
  ARRAY_DISTINCT(
    ARRAY_SELECT(
      $questionnaireRecordIDs, 
  	$(r, record) HASH_GET(HASH_GET($questionnaireHeaderData, $record), "Status")
  ));

  RETURN IF(
  	ARRAY_INDEXOF($allStatus, "Not Started") > 0, "Not Started",
    ARRAY_INDEXOF($allStatus, "In Progress") > 0, "In Progress",
    ARRAY_INDEXOF($allStatus, "Complete") > 0, "Complete",
    ARRAY_JOIN($allStatus, " ")
  )
);

LET $landingView := "Default";

RETURN 
HASH(
  "Category Filter", 			HASH_GET($eventargs, "Category Filter"),
  "View Mode", 					$viewMode,
  "Type", 						ARRAY($type),
  "Title", 						ARRAY($title),
  "Save Allowed", 				TRUE,
  "Questionnaire RecordIDs", 	ARRAY($questionnaireRecordIDs),
  "Questionnaire Header Data", 	ARRAY($questionnaireHeaderData), 
  "Questionnaire Status", 		ARRAY($questionnaireStatus), 
  "Landing View",				$landingView  								
)

