Column.Formula:
/*LET $approvalsDone := DB_LOOKUPALL("Approver Type", "Approval Event", "[Event Type] IN ('Approved', 'Endorsed', 'Cancelled')", HASH("parent", ARRAY(EFormType, RecordID)));

LET $approvalsNeeded := EACH [Approver Job-Title];

LET $approvalsToDo := 
ARRAY_WHERE(
    $approvalsNeeded,
    $(i, item) AND(
        [Current Approver Type] != $item, 
        ARRAY_INDEXOF($approvalsDone, $item) == NULL()
    )
);

RETURN ARRAY_FIRST($approvalsToDo)*/

LET $currentLine := ARRAY_INDEXOF(EACH [Approver Decision], "In Progress"); 
RETURN [Approver Job-Title]#($currentLine + 1)

LET $colours := HASH(
	"Submitted", "#48AA00",
	"Endorsed", "#48AA00",
	"Approved", "#48AA00",
	"Approved (Final)", "#48AA00",

	"In Progress", "#FFB100",
	"Pending", "#FFB100",

	"Not Endorsed", "#EA2E2E",
	"Rejected", "#EA2E2E",
	"Cancelled", "#EA2E2E"
);

LET $columns := 
ARRAY(
	"Approval GUID",
	"Approver Type", 
	"RecordID", 
	"Action Description", 
	"Event Type", 
	"Date of Event", 
	"Approver Notes", 
	"Performed on behalf of", 
	"Performed By"
);

LET $events := 
DB_LOOKUPALL(
    $columns,
	"Approval Event", 
	"[Event Type] <> 'Submitted'", 
	HASH("parent", ARRAY(EFormType, RecordID), "Sort", "[DateCreated] DESC")
);

LET $eventsHash := 
ARRAY_TO_HASH(
	$events, 
	$(i, event) ARRAY_FIRST($event), 
	$(i, event) INDEXES_TO_KEYS($event, $columns)
); 

LET $result := 
ARRAY_SELECT(
	EACH [Approval GUID], $(i, guid) 
	(
	    LET $event := HASH_GET($eventsHash, $guid);

		LET $approver := COALESCE(HASH_GET($event, "Action Description"), HASH_GET($event, "Performed on behalf of"));
		LET $approval := HASH_GET($event, "Approver Type");
		LET $approvalAssigned := [Approval Assigned]#$i;				
		LET $dateFormatted	:= DATEFORMAT(HASH_GET($event, "Date of Event"), "dd-MMM-yyyy HH:mm tt");
		LET $notes := HASH_GET($event, "Approver Notes");

		LET $performedBy := HASH_GET($event, "Performed By");
		LET $requiredApprover := HASH_GET($event, "Performed on behalf of"); 

		LET $url := IF($event <> NULL(), EBMS_GET_EFORMRECORD_URL("Approval Event", $recordID));

		LET $status := 
	  	IF(
			$event <> NULL(), 
				HASH_GET($event, "Event Type"),
			[Status]= "Cancelled", 
				"Cancelled",
			[Status]= "Rejected", 
				"Cancelled",
		    $approvalAssigned = TRUE, 
				"In Progress",
			"Pending"
		);


		RETURN 
		HASH(
			"Approver", 			$approver,
			"Job Title", 			$approval,
			"URL", 					$url, 
			"Colour", 				HASH_GET($colours, $status),
			"Status", 				$status,
			"Date", 					$dateFormatted,
			"Notes", 				$notes, 
			"Performed by",		$performedBy, 
			"Required approver", $requiredApprover
		)
	)
);

RETURN 
INDEXES_TO_KEYS(
  $result,
  EACH [Approval GUID]
)

LET $days := IF( OR( [Status] = "Approved", [Status] = "Rejected" ), [Days in Process],
ABS(WORKDAYSBETWEEN(NOW(), [Date Submitted])) );
RETURN IF( [Date Submitted] = NULL(), 0, $days )

IF(
  [Status] = "Cancelled", 
  NULL(), 
  (
	LET $thisComment := HASH_GET(HASH_GET([Approval History Data], [Approval GUID]), "Notes");

	RETURN
	IF( 
	  OR(
		[Approver Decision] = "Pending",
	    [Approver Decision] = "In Progress",
	  ), 
	  "", 
	  COALESCE($thisComment, "No comment provided")
	)
  )
)

LET $len := ARRAY_LENGTH([Parent Chain]);

LET $descriptionColumns := 
HASH(
	"Contract",  	"Contract Number", 
	"Vendor", 		"Company Name",
	"Procurement", 	"Procurement Number",
  	"Evaluation",	"Title",
	"Project", 		"Project Number"
); 

LET $getLabelText := $(eFormType, recordID)
(
	LET $targetColumn := HASH_GET($descriptionColumns, $eFormType); 
	RETURN 
	IF( 
		$targetColumn <> NULL(), 
		DB_LOOKUP(
			$targetColumn,
			$eFormType, 
			"[RecordID]=" & $recordID
		)
	)
); 

LET $parentLinks := 
ARRAY_SELECT([Parent Chain], $(i, item) 

    LET $eFormType 		:= ARRAY_FIRST($item);
    LET $recordID 			:= ARRAY_LAST($item);
    LET $hasReadAccess 	:= EBMS_GET_EFORMRECORD_PERMISSION($eFormType, $recordID, "read");
	LET $formHasDescriptiveLabel := ARRAY_INDEXOF(HASH_KEYS($descriptionColumns), $eFormType) > 0; 
    LET $showEFormType := $eFormType <> "Vendor";
	LET $descriptiveLabel := CALL($getLabelText, $eFormType, $recordID);

    RETURN ARRAY($eFormType, $descriptiveLabel)
); 

LET $result := 
ARRAY_FIRST(
	ARRAY_WHERE(
		ARRAY_REVERSE($parentLinks), $(i, item) 
		ARRAY_INDEX($item, 2) <> NULL()
	)
);

RETURN ARRAY_INDEX($result, 1) & " - " & ARRAY_INDEX($result, 2)

LET $set := EACH [Approval Type];
LET $firstSelection := ARRAY_INDEXOF($set, "DocuSign");
RETURN 
IF(
  [LineExists] = NULL(), NULL(), 

  [DocuSign Approval] <> TRUE, "In System",

  $firstSelection < [LineNumber], "DocuSign",

  COALESCE([Approval Type]#[LineNumber], "In System")
)

LET $audit := 
HASH_GET(
  HASH_GET(
	[Approval History Data], [Approval GUID]
  ), 
  "Approver"
);

RETURN 
COALESCE(
  $audit, 
  [Found Approver Name], 
  "Not found"
)

LET $previousApproverBatch := [Approval Batch]#([LineNumber] - 1);

LET $result := 
IF( 
  	AND(
	  	[Batch Approvals Enabled] <> TRUE,
	  	[LineExists] = TRUE
  	), 
  	[LineNumber], //Batch is always LineNumber if Batch Approvals are disabled

  	AND(
		[LineExists] = TRUE,
		OR(
		  	[Approval Batch] = NULL(),
		  	[Approval Type] = "DocuSign"
	  	)
  	), 
  	$previousApproverBatch + 1, //If null or Docusign, default to batch from previous line + 1

  	[Approval Batch]
);

RETURN $result

Column.ValidationFormula:
LET $approvalData := DB_LOOKUP(ARRAY("Status", "UUID"), "Submission", HASH("RecordID", [RecordID]));

LET $lineofGroupthatUserisIn := 
ARRAY_FIRST(
	ARRAY_WHERE(ARRAY_SELECT(
		EACH [Approver Decision],
		$(i, item) 
		IF(
			AND(
				EBMS_IS_USER_IN_ROLE(EBMS_GET_CURRENT_USER(), [Found Approver]#$i) = TRUE, 
				$item <> NULL
			) = TRUE, 
			$i
		)
	), $(i, item) $item <> NULL)
);

LET $thisUserApproverLine := 
ARRAY_INDEXOF(
  EACH [Found Approver], 
  EBMS_GET_CURRENT_USER()
);

LET $guid := 
  COALESCE(
	ARRAY_INDEX(EACH [Approval GUID], $lineofGroupthatUserisIn),
	ARRAY_INDEX(EACH [Approval GUID], $thisUserApproverLine)
);

LET $eventData := DB_LOOKUP("RecordID", "Approval Event", HASH("UUID", [UUID], "Approval GUID", $guid));

RETURN
IF( 
  $eventData <> NULL, 
  "This approval event has already been submitted, please refresh the page to see the decision", 

  OR(ARRAY_INDEX($data, 1) = "Rejected", ARRAY_INDEX($data, 1) = "Approved", ARRAY_INDEX($data, 1) = "Voided"), 
  "This approval event has already been submitted, please refresh the page to see the decision"
)

Column.Formula:
LET $assigned := 
ARRAY_WHERE(ARRAY_SELECT(
  EACH [Approver], $(i, item) 
  IF(ARRAY_INDEXOF([Assignee(s)], T($item)) > 0, $i)
), $(i, item) $item <> NULL);

LET $inProgress := 
ARRAY_WHERE(
	ARRAY_SELECT(
		$assigned, $(i, item) 
		IF(
			[Approver Decision]#$item = "In Progress", [Approver]#$item
		)
	), $(i, item) $item <> NULL
);

RETURN
ARRAY_INDEXOF(
  	ARRAY_SELECTMANY(
		$inProgress, $(i, item) 
		EBMS_GET_DESCENDANT_ROLES($item)
	  ), EBMS_GET_CURRENT_USER()
) > 0

LET $current := ARRAY([EFormType], [RecordID]);
LET $descriptionColumns := 
HASH(
	"Contract",  					"Contract Number", 
	"Vendor", 						"Company Name",
	"Procurement", 					"Procurement Number",
	"Project", 						"Project Number",
    "Evaluation", 		    		"Description",
  	"Information Request",  		"File No.", 
	"Subpoena", 					"File No.",
	"Privacy Incident", 			"File No."
); 

LET $altFormName:= HASH(
  	"Insurance",	"Compliance"
  );

LET $getLabelText := $(eFormType, recordID)
(
  	LET $targetColumn := HASH_GET($descriptionColumns, $eFormType);
  	RETURN 
  	IF( $targetColumn = NULL, NULL,
		LET $isthisRecord := ARRAY($eFormType, $recordID) = $current;
		LET $title:= 
		IF( $isthisRecord = TRUE, 
			CURRENT_VALUE($targetColumn),
			ARRAY_FIRST(DB_LOOKUP(
				ARRAY($targetColumn),
				$eFormType, 
				"[RecordID] FAST_EQUAL_TO " & $recordID
			))
		  );
		//shortern the descriptive label/ title
		LET $charCount := LEN($title);
		LET $titleArray := SPLIT($title, " ");
		LET $wordCount := ARRAY_LENGTH($titleArray);

		LET $charLimit := 30; 
		LET $wordLimit := ARRAY_LENGTH( SPLIT( LEFT($title, $charLimit), " ") );

	  RETURN //ARRAY_TAKE($titleArray, $wordLimit)
		IF(
			$wordCount <= $wordLimit, $title,
			ARRAY_JOIN(
				ARRAY_TAKE($titleArray, $wordLimit), " "
			)& " ..."
		)
	  )

); 

LET $getReadAccess := $(eFormType, recordID)
( EBMS_GET_EFORMRECORD_PERMISSION($eFormType, $recordID, "read"));

LET $currentDes := CALL($getLabelText, [EFormType], [RecordID]);
LET $currentAccess := FALSE;

RETURN
IF(
	[Parent Structure] <> NULL, [Parent Structure],
  	(

	  LET $parent := 
	  IF( 
		[RecordID] > 0, 
		(
		  LET $record := EBMS_GET_PARENT_RECORD(ARRAY_FIRST($current), ARRAY_INDEX($current, 2));
		  RETURN 
			  IF(  ARRAY_FIRST($record) == NULL, NULL,
				ARRAY_CONCAT( 
				  $record, 
				  CALL($getLabelText, ARRAY_FIRST($record), ARRAY_INDEX($record, 2)),
				  CALL($getReadAccess, ARRAY_FIRST($record), ARRAY_INDEX($record, 2))
				  )

			  )
	  )); 

	  LET $gParent := 
	  IF( 
		 $parent <> NULL, 
		(
		  LET $record := EBMS_GET_PARENT_RECORD(ARRAY_FIRST($parent), ARRAY_INDEX($parent, 2));
		  RETURN 
			  IF(  ARRAY_FIRST($record) == NULL, NULL,
				ARRAY_CONCAT( 
				  $record, 
				  CALL($getLabelText, ARRAY_FIRST($record), ARRAY_INDEX($record, 2)),
				  CALL($getReadAccess, ARRAY_FIRST($record), ARRAY_INDEX($record, 2))
			  )
		  )
	  )); 

	  LET $hash :=  
	  HASH_MERGE(
		  IF( $parent <> NULL, HASH("parent", 	$parent)),
		  IF( $gParent <> NULL, HASH("gparent", 	$gParent))
	  );

	  LET $parentStructure := 	//2nd instance
	  IF( 
		  ARRAY_LENGTH(HASH_KEYS($hash)) >0, $hash, NULL
	  );

	  LET $parentData:= 
	  ARRAY_WHERE(
		ARRAY(
			$gParent, 
			$parent, 
			ARRAY_CONCAT($current, $currentDes, $currentAccess),
		), $(i,item) $item <> NULL
	   );


	  LET $parentChain:=
		ARRAY_SELECT(
			  $parentData, $(i, item)
			  ARRAY_TAKE($item, 2)
	   );


	  LET $parentLinks :=
	  ARRAY_SELECT(
		  $parentData, $(i, item) 
			  LET $eFormType 		:= ARRAY_FIRST($item);
			  LET $recordID 		:= ARRAY_INDEX($item, 2);
			  LET $descriptiveLabel := ARRAY_INDEX($item, 3);
			  LET $hasReadAccess	:= ARRAY_INDEX($item, 4);
			  LET $URL	:= "records/" & URLENCODE($eFormType) & "/" & $recordID ;
			  LET $showEFormType := $eFormType <> "Vendor";

			  LET $labelColour := 
				  IF(
				   AND($eFormType = [EFormType], $recordID = [RecordID]),
					  "background: #ffffff; color: #5D5D5D;",
					 $hasReadAccess = TRUE,
					  "background: #ffffff; color: #4D87B7;",
					  "background: #ffffff; color: #5D5D5D;"

				  );



			  LET $labeltext:= 
					  IF( $showEFormType == TRUE, " " & COALESCE(HASH_GET($altFormName, $eFormType), $eFormType) ) &
					  IF(NOT(ISBLANK($descriptiveLabel)), " " & $descriptiveLabel);

			  LET $label :=  
				  "<span style=""margin-left: 5px;" & $labelColour & """>" &
				  HTMLENCODE($labeltext) & 
				  "</span>";

			  RETURN 
			  IF( 
				  $hasReadAccess == TRUE, 
				  "<a style=""text-decoration-line: none;"" href=" & HTMLENCODE($URL) & ">" & $label & "</a>",
				  $label 
			  )
	  );

	   LET $separator :=   "<svg style=""vertical-align: bottom; margin-left: 5px; height: 18px;"" xmlns=""http://www.w3.org/2000/svg"" viewBox=""0 0 256 512""><path fill=""#5D5D5D"" d=""M246.6 278.6c12.5-12.5 12.5-32.8 0-45.3l-128-128c-9.2-9.2-22.9-11.9-34.9-6.9s-19.8 16.6-19.8 29.6l0 256c0 12.9 7.8 24.6 19.8 29.6s25.7 2.2 34.9-6.9l128-128z""/></svg>";

	  LET $breadcrumb := "<div style=""height: 23px; padding-left: 5px; font-size: 10pt; color: #5D5D5D; "">You are here:" & ARRAY_JOIN($parentLinks, $separator) & "</div>";

	  RETURN 
	  HASH(
		  "Parent Structure",	$parentStructure,
		  "Parent Data", 		$parentData,
		  "Parent Chain",		$parentChain,
		  "Breadcrumb",		$breadcrumb
		)
	 )
)

Column.DefaultValueFormula:
LET $currentRoles := EBMS_GET_ANCESTOR_ROLES(EBMS_GET_CURRENT_USER());
RETURN 
INDEXES_TO_KEYS(
  ARRAY_SELECT($currentRoles, $(i, item) TRUE),
  $currentRoles
)

Column.Formula:
//Supported significant parents 
LET $SPList := 
ARRAY(
  "Contract", 
  "Procurement", 
  "Project"
); 

//find the match in parent chain that is closest proximity to this record
LET $match :=
ARRAY_WHERE(
  ARRAY_REVERSE([Parent Chain]), $(i, item) 
  ARRAY_INDEXOF($SPList, ARRAY_INDEX($item, 1)) > 0
);

//fallback to highest parent
LET $parent :=
COALESCE(
  ARRAY_FIRST($match), 
  ARRAY_FIRST([Parent Chain])
);

//return the parent EFormType
RETURN
ARRAY_INDEX($parent, 1)

//Supported significant parents 
LET $SPList := 
ARRAY(
  "Contract", 
  "Procurement", 
  "Project"
); 

//find the match in parent chain that is closest proximity to this record
LET $match :=
ARRAY_WHERE(
  ARRAY_REVERSE([Parent Chain]), $(i, item) 
  ARRAY_INDEXOF($SPList, ARRAY_INDEX($item, 1)) > 0
);

//fallback to highest parent
LET $parent :=
COALESCE(
  ARRAY_FIRST($match), 
  ARRAY_FIRST([Parent Chain])
);

//return the parent RecordID
RETURN
ARRAY_INDEX($parent, 2)

