LET $currentSequentialApprovalLine := ARRAY_INDEXOF(EACH [Approver Decision], "In Progress"); 
LET $thisUserApproverLine := ARRAY_INDEXOF(EACH [Found Approver], EBMS_GET_CURRENT_USER());

LET $guid := 
IF( 
  OR(
	EBMS_IS_USER_IN_ROLE(EBMS_GET_CURRENT_USER(), "Administrators"), 
	[Demo Mode] = TRUE
  ), 
  ARRAY_INDEX($guids, $currentSequentialApprovalLine),

  ARRAY_INDEX($guids, $thisUserApproverLine)
);

RETURN HASH("guid", $guid)

