FormulaDrivenChangeFieldValues.FieldValuesFormula:
LET $status:= DB_LOOKUP(
  	"Cancellation Status", 
  	"Approval Workflow", 
  	"[LineNumber] FAST_EQUAL_TO 1 AND [Submission Type] FAST_EQUAL_TO "& DB_ENCODECONSTANT([Type])
);

RETURN 
HASH(
  "Status", ARRAY(COALESCE($status,"Draft"))
)

