Navigate.UrlTemplate:
/records/{URLENCODE(
  COALESCE(
	ARRAY_FIRST([Return Record]), 
	ARRAY_FIRST([Review Record]), 
	ARRAY_FIRST([Parent Record])
  )
)}/{
URLENCODE(
  COALESCE(
	ARRAY_LAST([Return Record]), 
	ARRAY_LAST([Review Record]), 
	ARRAY_LAST([Parent Record])
  )
)
}

