FieldEntry.Formula:
LET $set := EACH [Approval Type];
LET $firstSelection := ARRAY_INDEXOF($set, "DocuSign");
LET $numberOfApprovers := ARRAY_LENGTH(EACH [Approval Type]);
LET $thisValue := [Approval Type];

RETURN 
ARRAY_SELECT(
  $set, $(i, item) 
  IF(
	//changing from docusign to standard
	AND(
	  $thisValue = "In System", 
      $i <= [LineNumber]
	), 
	"In System",


	//force all after this to DocuSign
	AND(
	  $thisValue = "DocuSign", 
      $i > $firstSelection
	), 
	"DocuSign",
    $item
  )
)

LET $set := EACH [Approval Batch];
LET $processSet := $(set)(
	LET $distinct := ARRAY_DISTINCT($set); 
	LET $numberOfItems := ARRAY_LENGTH($distinct);
	LET $range := RANGE(1, $numberOfItems); 

    RETURN 
    ARRAY_SELECT(
		$set, $(i, item) 
		LET $distinctIndex := ARRAY_INDEXOF($distinct, $item); 
		LET $result := ARRAY_INDEX($range, $distinctIndex); 

		LET $greatestInSet := GREATEST(ARRAY_TAKE($set, $i)); 
		LET $indexOfGreatest := ARRAY_INDEXOF($distinct, $greatestInSet);
		LET $largerValue := ARRAY_INDEX($range, $indexOfGreatest); 

		LET $lesserValueAfterThisIndex := 
		ARRAY_FIRST(
			ARRAY_WHERE(
				$set, $(thisIndex, thisItem) 
				AND(
					$thisIndex > $i, 
					$thisItem < $item
				)
			)
		);

		LET $forcedSequential := LEAST($i, GREATEST($result, $largerValue) + 1);

		LET $loopResult :=  
		IF( 
			[Approval Type] = "DocuSign", 
			$forcedSequential,

		//	$lesserValueAfterThisIndex <> NULL(), 
		//	$lesserValueAfterThisIndex, 

			GREATEST($result, $largerValue)
		);

		RETURN $loopResult
	)
);

LET $result := CALL($processSet, $set);
LET $doubleCheck := CALL($processSet, $result);
LET $tripleCheck := CALL($processSet, $doubleCheck);
RETURN $tripleCheck

LET $modules := 
DB_LOOKUPALL(
	"Module Settings EForm", 
	"Global Settings",
	"[Module Type] = 'Module'"
);

LET $primaryEntityNames := 
ARRAY_SELECT(
	$modules, $(m, module) 
	DB_LOOKUP(
		"Primary Entity Name", 
		 $module,
		 "[Primary Entity Name] IS NOT EMPTY"
	)
);

LET $parentChain := 
ARRAY_INDEX(
	ARRAY_TRANSPOSE([Parent Chain]), 
	1
);

LET $parentMatchingModuleName := 
ARRAY_WHERE(
	$parentChain, $(i, item) 
	AND(
		ARRAY_INDEXOF($primaryEntityNames, $item) > 0,
		$item <> "Submission"
	)
);

RETURN 
ARRAY_LAST($parentMatchingModuleName)
//If there are multiple matching names in the parent chain we will run the approval as if it belongs in the module that is the nearest parent to this record

LET $submissiontype :=
IF(
	OR( 
		ISBLANK([Type]),
		DB_LOOKUP(
			"RecordID", 
			"Approval Workflow", 
			"[Submission Type] = " & DB_ENCODECONSTANT([Type]) & " AND ([Custom Approval] <> TRUE OR [Source RecordID] = " & DB_ENCODECONSTANT(ARRAY_INDEX(ARRAY_FIRST([Parent Chain]),2)) &")",
		) = NULL()
	),	
  	"[Submission Type] = 'Master List - " & [Module] & "'", //fallback
	"[Submission Type]=" & DB_ENCODECONSTANT([Type])
);


LET $filters := 
ARRAY(
    "([Upper Price] IS NULL OR [Upper Price]>" & DB_ENCODECONSTANT(COALESCE([Submission Value], [Estimated Contract Value], 0)) & ")",
    "([Lower Price] IS NULL OR [Lower Price]<=" & DB_ENCODECONSTANT(COALESCE([Submission Value], [Estimated Contract Value], 0)) & ")",
    $submissiontype
);

LET $filterstring := ARRAY_JOIN(ARRAY_WHERE($filters, $(i, item) $item != NULL()), " AND ");

LET $lookup := 
DB_LOOKUPALL(
	  ARRAY(
	  "Source EFormType", 
	  "Source RecordID",
	  "Who?",
	  "Approval Type"
    ), 
    "Approval Workflow", 
    $filterstring
);

LET $matches := 
ARRAY_SELECTMANY(
	[Parent Chain], $(i, item)
	ARRAY_WHERE(
		$lookup, $(j, jitem) 
		AND(
			ARRAY_INDEX($item, 1) = ARRAY_INDEX($jitem, 1),
			ARRAY_INDEX($item, 2) = ARRAY_INDEX($jitem, 2)
		)
	)
);

LET $customRoute := ARRAY_WHERE($matches, $(i, item) $item <> ARRAY());

RETURN 
IF( 
	$customRoute <> ARRAY(), 
	ARRAY_SELECT($customRoute, $(i, item) ARRAY(ARRAY_INDEX($item, 3), ARRAY_INDEX($item, 4))), 
	ARRAY_SELECT(ARRAY_WHERE($lookup, $(i, item) AND(ARRAY_INDEX($item, 1) = NULL(), ARRAY_INDEX($item, 2) = NULL())), $(i,item) ARRAY(ARRAY_INDEX($item, 3), ARRAY_INDEX($item, 4)))
)

LET $functions:=
HASH(
//	"GET_FINANCIAL_DELEGATE_IN_GROUP", $(target)
//      IFERROR(
//         (
//		   LET $usersInGroup := 
//           ARRAY_SELECT(
//             EBMS_GET_DESCENDANT_ROLES($target), $(i, item) 
//             ARRAY(
//			   $item,
//               EBMS_GET_ROLE_FINANCIAL_APPROVAL_LIMIT($item)
//             )
//           );
//		   
//		   LET $usersWithDelegationAboveLimit := 
//           ARRAY_WHERE(
//             $usersInGroup, $(i, item) 
//             AND(
//               ARRAY_INDEX($item, 1) <> $target,
//               ARRAY_INDEX($item, 1) <> "system",
//               ARRAY_INDEX($item, 1) <> EBMS_GET_CURRENT_USER(),
//               ARRAY_INDEX($item, 2) > [Submission Value]
//             )
//           );
//         
//           LET $sorted := 
//		   ARRAY_SORT($usersWithDelegationAboveLimit, $(item) ARRAY_INDEX($item, 2));
//		    
//		   RETURN ARRAY_FIRST($sorted)
//		), NULL()
//  ),


  "GET_USER_FROM_PARENT", $(target)
		IFERROR(
			DB_LOOKUP(
				$target, 
				ARRAY_INDEX([Parent Record], 1), 
				HASH("RecordID", ARRAY_INDEX([Parent Record], 2))
			), NULL()
		),


	"GET_USER_IN_ASSIGNMENT_FROM_SIGNIFICANT_PARENT", $(target)
		IFERROR(
			IF( 
				[Module] <> NULL, 
				(
					LET $parentData := ARRAY_TRANSPOSE([Parent Chain]);
					LET $moduleIndex := ARRAY_INDEXOF(ARRAY_INDEX($parentData, 1), [Module]);
					LET $significantParentRecordID := ARRAY_INDEX(ARRAY_INDEX($parentData, 2), $moduleIndex);

					LET $result := 
					DB_LOOKUPALL(
						"Username in Role", //"Username in Role", 
						[Module], 
						HASH(
							"RecordID", $significantParentRecordID, 
							"Role", $target
						)
					);
					RETURN $result
				), 
				NULL
			), 
			NULL()
		),

    "ASSIGN_GROUP", $(target)
		$target,

	"GET_USER_IN_ROLE", $(target)
		//assumes there is only one child that is legitimate
		IFERROR(
			ARRAY_FIRST(
				ARRAY_WHERE(
					EBMS_GET_DESCENDANT_ROLES($target), $(i, item) 
					AND(
					  $item <> $target,
					 $item <> "system"
					)
				)
			), NULL()
		),

	"GET_USER_IN_POSITION", $(target)
		IFERROR(
			DB_LOOKUP(
				"Username", 
				"sys.Users",
				"GET_USER_POSITION([Username]) = " & DB_ENCODECONSTANT($target)
			),
			NULL()
		),

	"EXTERNAL_USER", $(target)
		IFERROR(
			$target,
			NULL()
		),

  	"GET_USER_IN_POSITION_AND_DEPARTMENT", $(target)
		//$target is an array of positions
		//departments check is inclusive of department and upwards of department
		IFERROR(
			DEPT_FIND_UPWARDS_FOR_DEPARTMENT(EBMS_GET_USER_DEPARTMENT(EBMS_GET_CURRENT_USER()), $target), 
			NULL()
		),

  	"GET_USER_IN_POSITION_AND_SIGNIFICANT_PARENT_DEPARTMENT", $(target)
	IFERROR(
		LET $parentData := ARRAY_TRANSPOSE([Parent Chain]);
		LET $moduleIndex := ARRAY_INDEXOF(ARRAY_INDEX($parentData, 1), [Module]);
		LET $significantParentRecordID := ARRAY_INDEX(ARRAY_INDEX($parentData, 2), $moduleIndex);

		LET $department := 
		DB_LOOKUP(
			"Department",
			[Module], 
			HASH("RecordID", $significantParentRecordID)
		);
		RETURN

		DEPT_FIND_UPWARDS_FOR_DEPARTMENT($department, $target),
	NULL()
	),

  	"GET_USER_SUPERVISOR", $()
  		IFERROR(
		  	ROLE(EBMS_GET_SUPERVISOR(EBMS_GET_CURRENT_USER())),
			NULL()
		),

   "GET_USER_IN_GROUP_NAMED_DEPARTMENT_PLUS_CUSTOMSTRING", $(target)
		IFERROR(
			IF( 
				[Module] <> NULL, 
			    (
				  LET $parentData := ARRAY_TRANSPOSE([Parent Chain]);
				  LET $moduleIndex := ARRAY_INDEXOF(ARRAY_INDEX($parentData, 1), [Module]);
				  LET $significantParentRecordID := ARRAY_INDEX(ARRAY_INDEX($parentData, 2), $moduleIndex);

  				  LET $department := 
				  DB_LOOKUP(
				  	"Department",
				  	[Module], 
				  	HASH("RecordID", $significantParentRecordID)
				  );

				  LET $groupName := $department & " - " & $target;
				  LET $groupExists := $groupName = ROLE($groupName); 

				  LET $approver := 
				  IF(
				  	  $groupExists = TRUE, ARRAY_FIRST(ARRAY_WHERE(EBMS_GET_DESCENDANT_ROLES($groupName), $(i, item) EBMS_CONTAINS($item, $groupName) <> TRUE)),
				      $groupExists <> TRUE, NULL 
				  );

				  LET $backupApprover := 
				  IF(
				  	  AND($groupExists = TRUE, $approver = NULL), 
					    ARRAY_FIRST(ARRAY_WHERE(
						  EBMS_GET_DESCENDANT_ROLES(EBMS_GET_ANCESTOR_ROLES($groupName)), $(i, item) 
						  EBMS_CONTAINS($item, $groupName) <> TRUE
						))
				  );
				  RETURN
				  IF(
					$approver <> NULL, 
					  $approver, 

					$backupApprover <> NULL, 
				      $backupApprover,
					NULL
				  )
			    )
			),
			NULL
		), 

  	"GET_USER_IN_GROUP_AND_IN_SIGNIFICANT_PARENT_DEPARTMENT", $(target)
		IFERROR(
			IF( 
				[Module] <> NULL, 
				(
					LET $parentData := ARRAY_TRANSPOSE([Parent Chain]);
					LET $moduleIndex := ARRAY_INDEXOF(ARRAY_INDEX($parentData, 1), [Module]);
					LET $significantParentRecordID := ARRAY_INDEX(ARRAY_INDEX($parentData, 2), $moduleIndex);

					LET $department := 
					DB_LOOKUP(
						"Department", //"Username in Role", 
						[Module], 
						HASH("RecordID", $significantParentRecordID)
					);
				  	LET $approver :=
					ARRAY_FIRST(ARRAY_WHERE(
						EBMS_GET_DESCENDANT_ROLES($target), $(i, item) 
						AND(
		   					$item <> $target,
		   					$item <> "system",
		   					EBMS_GET_USER_DEPARTMENT($item) = $department
						)
					));
				  	//fallback if no user exists in group for listed department
				  	LET $backupApprover :=
					IF(
						$approver = NULL,
					  	LET $departmentChain := ARRAY_SELECT(DEPT_COLLECT_CONTAINERS($department), $(i,item) HASH_GET($item, "name")); //gets parent department chain from parent department
						RETURN
					  	//returns all users in group who are in a department in the hierarchy, sorts descending by index of the hierarchy, then returns the first user in the sorted array
						ARRAY_FIRST(ARRAY_SORTDESC(ARRAY_WHERE(
							EBMS_GET_DESCENDANT_ROLES($target), $(i,item)
							AND(
								$item <> $target,
								$item <> "system",
								ARRAY_INDEXOF($departmentChain, EBMS_GET_USER_DEPARTMENT($item)) > 0
							)
						)), $(item) ARRAY_INDEXOF(EBMS_GET_USER_DEPARTMENT($item), $departmentChain))
					);

					RETURN
					IF(
						$approver <> NULL, $approver,
						$backupApprover <> NULL, $backupApprover,
						NULL
					)
				), 
				NULL
			), 
			NULL()
		),

  	"GET_PANEL_MEMBERS", $()
  		LET $contextRecordID := IF([Parent EFormType] = "Procurement", ARRAY_FIRST(DB_LOOKUP("Evaluation RecordIDs", "Procurement", "[RecordID] = " & DB_ENCODECONSTANT([Parent RecordID]))), [Parent RecordID]);
	  	RETURN
	 	 IFERROR(
				  DB_LOOKUPALL(
			  		"Panel Member Username",
			  		"Evaluation",
			  		"[RecordID] = " & DB_ENCODECONSTANT($contextRecordID) & " AND [Panel Member Username] IS NOT EMPTY"
				),
				NULL()
			),

  "GET_VENDOR", $()
		IFERROR(
			LET $module := "Contract";
			LET $parentData := ARRAY_TRANSPOSE([Parent Chain]);
			LET $moduleIndex := ARRAY_INDEXOF(ARRAY_INDEX($parentData, 1), $module);
			LET $significantParentRecordID := ARRAY_INDEX(ARRAY_INDEX($parentData, 2), $moduleIndex);

			LET $vendorRecordID := 
				DB_LOOKUP(
					"Vendor RecordID",
					$module,
					HASH(
						"RecordID", $significantParentRecordID
					)
				);

			LET $result :=
				DB_LOOKUPALL(
					"Username",
					"Vendor",
					"[RecordID] = " & DB_ENCODECONSTANT($vendorRecordID) & " AND [LineNumber] = 1"
			);
			RETURN $result,
			NULL()
		)

);	

//----------------------------END SHORTCUTS----------------------------

LET $approverTypeDisplayName := 
HASH(
  "Panel Members", "Panel Member"
); 

LET $approvers :=
ARRAY_SELECTMANY(
    [Approver Types Required], $(i, item)
    LET $approverType := ARRAY_FIRST($item); //role
    LET $approvalType := ARRAY_INDEX($item, 2); // in system or docusign

    LET $data :=
    DB_LOOKUP(
        ARRAY("Method", "Arguments", "Who"),
        "Settings - Approval",
        "[Who] = " & DB_ENCODECONSTANT($approverType)
    );
    //ARRAY("GET_USER_IN_POSITION", "CEO")
    LET $method := ARRAY_INDEX($data, 1);
    LET $arguments := ARRAY_INDEX($data, 2);

	LET $user:= 
    CALL(HASH_GET($functions, $method), $arguments);

    RETURN
	ARRAY_SELECT(
	  	$user, $(i, item) 
	  	ARRAY(
	    	//display name
		    IF( 
   				ISERROR(EBMS_GET_ROLE_DISPLAYNAME($item)), 
				  "Not found",
			    EBMS_GET_ROLE_DISPLAYNAME($item)
			), 	

			//username
		    IF( 
   				ISERROR($item),
			      "Not found",
			    $item
			),


			//role name
		    IF( 
          		ISERROR($item), 
            		$approverType & " - Role '" & $approverType & "' not defined. Refer to Approval Workflow settings.",
          		COALESCE(HASH_GET($approverTypeDisplayName, $approverType), $approverType)   
	    	), 
	    	$method, 	//method
		  	$approvalType //in system or docusign
		)	
	  )
	);

LET $escalateUsingThresholds := 
DB_LOOKUP(
	"User-Based Approval Thresholds", 
	"Settings - Approval", 
	"[RecordID] = 1"
);

LET $includeAllEscalationSteps := 
DB_LOOKUP(
	"Include Escalation Steps", 
	"Settings - Approval", 
	"[RecordID] = 1"
);

LET $escalate := $(approver, approverList, i)
(

	LET $displayName := 	ARRAY_INDEX($approver, 1); //display name
	LET $user := 			ARRAY_INDEX($approver, 2); //username
	LET $approverType:= 	ARRAY_INDEX($approver, 3); //role name
	LET $method := 			ARRAY_INDEX($approver, 4); //method
	LET $approvalType := 	ARRAY_INDEX($approver, 5); //in system or docusign

	LET $financialApprovalLimit := EBMS_GET_ROLE_FINANCIAL_APPROVAL_LIMIT($user); 

	LET $supervisor := ROLE(EBMS_GET_SUPERVISOR($user));

	LET $supervisorData := 
	ARRAY(
		EBMS_GET_ROLE_DISPLAYNAME($supervisor), 
		$supervisor, 
		COALESCE(EBMS_GET_USER_POSITION($supervisor), "Supervisor"), 
		"ESCALATE"
	);

	LET $newApproverList := ARRAY_CONCAT($approverList, ARRAY($supervisorData));

	LET $submissionAboveLimit := $financialApprovalLimit < [Submission Value];

	RETURN
	IF( 
		AND(
		  $submissionAboveLimit = TRUE, 
		  $supervisor <> NULL(),
		   $supervisor <> $user
		),

		CALL(
			$escalate, 
			$supervisorData, 
			$newApproverList, 
			$i + 1
		),

		IF( 
			$includeAllEscalationSteps = TRUE, 
			$approverList, 
			ARRAY(ARRAY_LAST($approverList))
		)
	)
);

LET $escalations := 
IF( 
	$escalateUsingThresholds = TRUE, 
	CALL(
		$escalate, 
		ARRAY_LAST($approvers), 
		ARRAY(ARRAY_LAST($approvers)), 
		1
	)
);

LET $result :=  
ARRAY_CONCAT(
  $approvers, 
  $escalations
);

RETURN 
ARRAY_WHERE($result, $(i, item) $item <> NULL())

LET $types := 
ARRAY_INDEX(
  ARRAY_TRANSPOSE(
	[Approver Types Required]
  ), 2
);

RETURN
ARRAY_INDEXOF($types, "DocuSign") > 0

LET $approvers := [Approver Type to User Map];

LET $checkList := ARRAY_SELECT($approvers, $(i, item) ARRAY(ARRAY_INDEX($item, 2), ARRAY_LAST($item))); //username and approvaltype

LET $distinct := 
ARRAY_WHERE(
	$approvers, $(i, item)
	LET $arrayToCheck := ARRAY_SKIP($checkList, $i);
	LET $lastOccurence :=  ARRAY_INDEXOF($arrayToCheck, ARRAY(ARRAY_INDEX($item, 2), ARRAY_LAST($item))) = NULL();
	LET $isOriginator := ARRAY_INDEX($item, 2) = [Originator];

	RETURN
	AND(
		$lastOccurence = TRUE, 
		$isOriginator <> TRUE
	)
);

RETURN
ARRAY_TRANSPOSE($distinct)

LET $hash := 
HASH(
  "EXTERNAL_USER", "External"
);

RETURN
IF( 
  [Approver Data] = ARRAY(), 
  ARRAY("System User"), 
  ARRAY_SELECT(
    ARRAY_INDEX([Approver Data], 4), $(i, item) 
    COALESCE(HASH_GET($hash, $item), "System User")
  )
)

LET $guid := HASH_GET($eventargs, "guid"); 
LET $line := ARRAY_INDEXOF(EACH [Approval GUID], $guid);
RETURN [Approver Job-Title]#$line

LET $guid := HASH_GET($eventargs, "guid"); 
LET $line := ARRAY_INDEXOF(EACH [Approval GUID], $guid);
LET $user := [Found Approver]#$line;
LET $docuSign := [Approval Type]#$line;

RETURN
IF( 
	EBMS_GET_CURRENT_ACTUAL_USER() == "External", $user,
  	$docuSign == "DocuSign", $user,
	EBMS_GET_CURRENT_ACTUAL_USER()
)

LET $guid := HASH_GET($eventargs, "guid"); 
LET $line := ARRAY_INDEXOF(EACH [Approval GUID], $guid);
RETURN [Found Approver]#$line

LET $guid := HASH_GET($eventargs, "guid"); 
LET $line := ARRAY_INDEXOF(EACH [Approval GUID], $guid);
RETURN [Found Approver Name]#$line

LET $guid := HASH_GET($eventargs, "guid"); 
LET $line := ARRAY_INDEXOF(EACH [Approval GUID], $guid);
LET $user := [Found Approver]#$line;
LET $docuSign := ARRAY_INDEXOF(EACH [Approval Type], $guid);

RETURN
IF( 
	EBMS_GET_CURRENT_ACTUAL_USER() == "External", 	EBMS_GET_ROLE_DISPLAYNAME($user),
	$docuSign == "DocuSign", EBMS_GET_ROLE_DISPLAYNAME($user),
	EBMS_GET_ROLE_DISPLAYNAME(EBMS_GET_CURRENT_ACTUAL_USER())
)

LET $guid := HASH_GET($eventargs, "guid"); 
LET $line := ARRAY_INDEXOF(EACH [Approval GUID], $guid);
RETURN [Approver is System User]#$line

LET $guid := HASH_GET($eventargs, "guid"); 
LET $line := ARRAY_INDEXOF(EACH [Approval GUID], $guid);
RETURN [Approval Type]#$line

LET $guid := HASH_GET($eventargs, "guid"); 
LET $line := ARRAY_INDEXOF(EACH [Approval GUID], $guid);
RETURN [Found Approver]#$line

LET $guid := HASH_GET($eventargs, "guid"); 
LET $line := ARRAY_INDEXOF(EACH [Approval GUID], $guid);
RETURN [Found Approver Name]#$line

TriggerCustomEvent.EventArgumentsFormula:
LET $email := ARRAY_INDEX([Assignee Email Addresses], $count);
LET $approver := ARRAY_INDEX([Assignee(s)], $count);
LET $linenumber := ARRAY_INDEX([Assigned Lines], $count);
LET $systemUser :=  [Approver is System User]#$linenumber;
LET $pdfsettings:=  IF( 
  					[Parent Record] = NULL, NULL,
					  DB_LOOKUP(
								  ARRAY("PDF Feature Enabled", "PDF View Name"),
								  "Approval Workflow", 
								  HASH("Submission Type", [Type], "LineNumber", 1)
						  )
  					);

LET $submitterNotes := 
					IF(
					  	OR(
						  	$systemUser <> TRUE,
						  	EBMS_GET_USER_TYPE($approver) == "GUEST"
						),
					  	[Submitter Notes], 
					  	[Internal Submitter Notes]
					);

RETURN 
HASH(
  "emailAddress", $email,
  "approver", $approver,
  "linenumber", $linenumber, 
  "guid", [Approval GUID]#$linenumber,
  "systemuser", $systemUser,
  "approvaltype", [Approval Type]#$linenumber,
  "submitterNotes", $submitterNotes,
  "attachpdf", ARRAY_INDEX($pdfsettings, 1),
  "pdfview", ARRAY_INDEX($pdfsettings, 2),
  "attached", EBMS_GET_EFORMRECORD_ATTACHMENT_COUNT(EFormType, RecordID, "Submission PDF")
)

