FormulaCriterion.Formula:
LET $decisions := EACH [Approver Decision]; 
LET $lineMatch := ARRAY_INDEXOF(EACH [Approval GUID], HASH_GET($eventargs, "guid"));
LET $lineInProgress := [Approver Decision]#$lineMatch = "In Progress";

LET $moreThanOneInProgress := ARRAY_LENGTH(ARRAY_WHERE(EACH [Approver Decision], $(i, item) $item = "In Progress")) > 1; 
LET $nonePending := ARRAY_INDEXOF($decisions, "Pending") = NULL(); 

RETURN 
AND(
  $moreThanOneInProgress <> TRUE, 
  $nonePending = TRUE, 
  [Assigned Approval Batch] = GREATEST(EACH [Approval Batch]),
  $lineMatch > 0,
  $lineInProgress = TRUE
)

LET $decisions := EACH [Approver Decision]; 
LET $lineMatch := ARRAY_INDEXOF(EACH [Approval GUID], HASH_GET($eventargs, "guid"));
LET $lineInProgress := [Approver Decision]#$lineMatch = "In Progress";

LET $moreThanOneInProgress := ARRAY_LENGTH(ARRAY_WHERE(EACH [Approver Decision], $(i, item) $item = "In Progress")) > 1; 
LET $nonePending := ARRAY_INDEXOF($decisions, "Pending") = NULL(); 

RETURN 
AND(
  OR(
	$nonePending <> TRUE, 
    $moreThanOneInProgress = TRUE,
  ), 
  $lineMatch > 0,
  $lineInProgress = TRUE
)

LET $decisions := EACH [Approver Decision]; 
LET $inProgressLine := ARRAY_INDEXOF($decisions, "In Progress"); 
LET $GUIDofProgressLine := [Approval GUID]#$inProgressLine; 

RETURN 
$GUIDofProgressLine = HASH_GET($eventargs, "guid")

