TriggerCustomEvent.EventArgumentsFormula:
LET $guids := EACH [Approval GUID]; 

LET $currentSequentialApprovalLine := ARRAY_INDEXOF(EACH [Approver Decision], "In Progress"); 

LET $thisUserApproverLine := 
ARRAY_INDEXOF(
  EACH [Found Approver], 
  EBMS_GET_CURRENT_USER()
  //problem that ths is pulling the current user, but if the user is in a group then it gets nothing
);

LET $lineofGroupthatUserisIn := 
ARRAY_FIRST(ARRAY_WHERE(ARRAY_SELECT(EACH [Approver Decision], $(i, item) IF($item = "In Progress", $i)), $(i, item) $item <> NULL));

LET $guid := 
IF( 
  OR(
	EBMS_IS_USER_IN_ROLE(EBMS_GET_CURRENT_USER(), "Administrators"), 
	[Demo Mode] = TRUE
  ), 
  ARRAY_INDEX($guids, $currentSequentialApprovalLine),

  COALESCE(
	ARRAY_INDEX($guids, $thisUserApproverLine), 
	ARRAY_INDEX($guids, $lineofGroupthatUserisIn)
  )
  //get guid of the group that the user is in
);

RETURN HASH("guid", $guid)

LET $guids := EACH [Approval GUID]; 

LET $currentSequentialApprovalLine := ARRAY_INDEXOF(EACH [Approver Decision], "In Progress"); 

LET $thisUserApproverLine := 
ARRAY_INDEXOF(
  EACH [Found Approver], 
  EBMS_GET_CURRENT_USER()
  //problem that ths is pulling the current user, but if the user is in a group then it gets nothing
);

LET $lineofGroupthatUserisIn := 
ARRAY_FIRST(ARRAY_WHERE(ARRAY_SELECT(EACH [Approver Decision], $(i, item) IF($item = "In Progress", $i)), $(i, item) $item <> NULL));

LET $guid := 
IF( 
  OR(
	EBMS_IS_USER_IN_ROLE(EBMS_GET_CURRENT_USER(), "Administrators"), 
	[Demo Mode] = TRUE
  ), 

  COALESCE(
	ARRAY_INDEX($guids, $thisUserApproverLine),
	ARRAY_INDEX($guids, $currentSequentialApprovalLine)
  ), 

  COALESCE(
	ARRAY_INDEX($guids, $thisUserApproverLine), 
	ARRAY_INDEX($guids, $lineofGroupthatUserisIn)
  )
  //get guid of the group that the user is in
);

RETURN HASH("guid", $guid)

LET $decisions := EACH [Approver Decision]; 
