  ARRAY(
	EACH [Approval Type],
    EACH [Approval GUID]
  )
);

LET $matches :=
ARRAY_WHERE($data, $(i, item) ARRAY_INDEX($item, 1) = "DocuSign");

RETURN 
ARRAY_INDEX(ARRAY_TRANSPOSE($matches), 2)

LET $data :=
ARRAY_TRANSPOSE(
  ARRAY(
	EACH [Approval Type],
    EACH [Found Approver]
  )
);

LET $matches :=
ARRAY_WHERE($data, $(i, item) ARRAY_INDEX($item, 1) = "DocuSign");

RETURN 
ARRAY_INDEX(ARRAY_TRANSPOSE($matches), 2)

LET $data :=
ARRAY_TRANSPOSE(
  ARRAY(
	EACH [Approval Type],
    EACH [Found Approver Name]
  )
);

LET $matches :=
ARRAY_WHERE($data, $(i, item) ARRAY_INDEX($item, 1) = "DocuSign");

RETURN 
ARRAY_INDEX(ARRAY_TRANSPOSE($matches), 2)

LET $notSystemUser := 
ARRAY_SELECT(
  EACH [Approver is System User], $(i, item)
  NOT($item)
); 

LET $data :=
ARRAY_TRANSPOSE(
  ARRAY(
	EACH [Approval Type],
    $notSystemUser
  )
);

LET $matches :=
ARRAY_WHERE($data, $(i, item) ARRAY_INDEX($item, 1) = "DocuSign");

RETURN 
ARRAY_INDEX(ARRAY_TRANSPOSE($matches), 2)

LET $demoMode := DB_LOOKUP("Demo Mode", "Global Settings", "[RecordID] = 1");

LET $userEmailAddress:= EBMS_GET_ROLE_EMAIL_ADDRESSES(EBMS_GET_CURRENT_USER(), TRUE);


LET $data :=
ARRAY_TRANSPOSE(
  ARRAY(
	EACH [Approval Type],
    EACH [Approver Email Address]
  )
);

LET $matches :=
ARRAY_WHERE($data, $(i, item) ARRAY_INDEX($item, 1) = "DocuSign");

LET $emailAddresses  := 
ARRAY_INDEX(ARRAY_TRANSPOSE($matches), 2);

LET $demoEmail:=  DB_LOOKUP("DocuSign Email", "Settings - Approval", "[RecordID] = 1");

LET $result := 
ARRAY_SELECT(
  $emailAddresses, $(i, item) 
  IF(
	AND(
	  $demoMode = TRUE//, 
	//  $userEmailAddress = NULL()
	), 
	$demoEmail,

	COALESCE(
		$item,
		EBMS_GET_ROLE_EMAIL_ADDRESSES($item, TRUE)
	 )
  )
);

RETURN $result

LET $data :=
ARRAY_TRANSPOSE(
  ARRAY(
	EACH [Approval Type],
    EACH [Approver Mobile Number]
  )
);

LET $matches :=
ARRAY_WHERE($data, $(i, item) ARRAY_INDEX($item, 1) = "DocuSign");

RETURN 
ARRAY_INDEX(ARRAY_TRANSPOSE($matches), 2)

LET $anyInProgress := 
ARRAY_LENGTH(ARRAY_WHERE(
  	EACH [Approver Decision], $(i,decision)
  	AND(
	  	//check the LineNumber is the currently assigned approval batch
	  	ARRAY_INDEX(EACH [Approval Batch], $i) = [Assigned Approval Batch],
	  	//check the approval status is either Pending or In Progress
  		OR(
		  	$decision = "Pending",
		  	$decision = "In Progress"
		)
  	)
)) > 0;

RETURN 
IF( 
  $anyInProgress <> TRUE, 
  //if no approvals are in progress, return the next batch from the list
  MIN(ARRAY_WHERE(EACH [Approval Batch], $(i,batch) $batch > [Assigned Approval Batch])),
  [Assigned Approval Batch]
)

LET $set :=
ARRAY_SELECT(
  EACH [Approval Batch], $(i, batch) 
  IF( 
	$batch = [Assigned Approval Batch], $i
  )
);

RETURN 
ARRAY_WHERE($set, $(i, item) $item <> NULL())

LET $set := ARRAY_SELECT( EACH [Approval Batch], $(i, batch) IF( AND( $batch = [Assigned Approval Batch], [Approver is System User]#$i = TRUE ), T([Approver]#$i),
AND( $batch = [Assigned Approval Batch], [Approver is System User]#$i <> TRUE ), "external" ) );
RETURN ARRAY_WHERE($set, $(i, item) $item <> NULL())

LET $set :=
ARRAY_SELECT(
  EACH [Approval Batch], $(i, batch) 
  IF( 
	$batch = [Assigned Approval Batch], 
	[Approver Email Address]#$i
  )
);

RETURN 
ARRAY_WHERE($set, $(i, item) $item <> NULL())
