Column.Formula:
LET $recordID := [RecordID];

LET $thisYear :=  RIGHT(FINANCIALYEAR([DateCreated]), 2);

LET $fy := $thisYear-1 &"-"& $thisYear;

RETURN $recordID &" of "& $fy

Column.ValidationFormula:
LET $phone := [Phone] ;

LET $validate :=	IF( AND( REGEX_MATCH($phone, "^([0-9]{10})$") <> NULL(), ISERROR(N($phone)) <> TRUE, $phone  <> NULL() ), TRUE, FALSE );

RETURN IF([Responsible Party] = "Freedom of Information Department", TRUE,
  		AND([Responsible Party] <> "Freedom of Information Department", $thisValue = NULL() ), TRUE,
	  ,$validate)

Column.Formula:
LET $allDates := DB_LOOKUPALL(
	"Due Date",
	"Information Request Follow Up",
	"[LineNumber] = 1 AND [Status] = 'To Do' AND [Due Date] <> NULL ",
	HASH("parent", ARRAY(EFormType, RecordID), "sort", "[Due Date]")
);


LET $noFollowUp :=  IF(
	AND(ARRAY_LENGTH($allDates)=0, [Status] <> "Closed", NOT(ISBLANK([Return Date]))), 
	D([Return Date] - 7),
	NULL()
);

LET $today := TODAY();

LET $nextDueDate := ARRAY_FIRST(
	ARRAY_WHERE(
		$allDates, $(i, item)
		$today <= $item
	)
);

RETURN COALESCE(ARRAY_FIRST($allDates), $noFollowUp)

LET $a := EACH[Responsible Party];

LET $freedomOfInformation := ARRAY_WHERE($a, $(i, item) $item = "Freedom of Information Department");

RETURN IF(ARRAY_LENGTH($freedomOfInformation)>0, TRUE, FALSE)

LET $partyEmail := DB_LOOKUP(
	"Email",
	"Information Request Organisation",
	HASH("LineNumber",1, "Organisation Type", "Agent", "Organisation Name", [Responsible Party])
);  


LET $userEmail :=EBMS_GET_ROLE_EMAIL_ADDRESSES([Contact], TRUE);

RETURN COALESCE($partyEmail,$userEmail)

