  AND(
    [Mail Merge Options - Document Selection] <> NULL, 
    HASH_GET([Mail Merge Options - Document Selection], "FileName") <> [Mail Merge Purpose]  
  )
)

LET $internal := "Freedom of Information Department";

LET $agents := DB_LOOKUPALL(
	"Organisation Name",
	"Information Request Organisation",
	HASH("LineNumber",1, "Organisation Type", "Agent")
);

RETURN ARRAY_CONCAT($internal, $agents)

LET $data1 :=ARRAY_WHERE(
    EBMS_GET_DESCENDANT_ROLES("Assigned Officers"), $(i, item) $item <> "Assigned Officers"

); 

LET $officers := ARRAY_SELECT($data1, $(i, item) EBMS_GET_ROLE_DISPLAYNAME($item) );

LET $data2 :=ARRAY_WHERE(
    EBMS_GET_DESCENDANT_ROLES("Freedom of Information Managers"), $(i, item) $item <> "Freedom of Information Managers"

); 

LET $managers := ARRAY_SELECT($data2, $(i, item) EBMS_GET_ROLE_DISPLAYNAME($item) );

LET $internal := ARRAY_DISTINCT(ARRAY_CONCAT($managers, $officers));

LET $agentContact := DB_LOOKUPALL(
	"Contact Name",
	"Information Request Contact",
	HASH("LineNumber",1, "OrganisationID", [Responsible Party RecordID])
);


RETURN IF([Responsible Party]="Freedom of Information Department", $internal, $agentContact )

FieldEntry.Formula:
LET $noRP :=  ARRAY_LENGTH(EACH[Responsible Party]);   

LET $privilegeClaimed := EACH[Privilege Claimed];   

LET $noClaimed := 
	ARRAY_SELECT(
			ARRAY_WHERE(
				$privilegeClaimed, 	$(i, item) $item = "No" ),
			$(i, item) $item 
			);    

RETURN IF(ARRAY_LENGTH($noClaimed) = $noRP, "Closed", [Status])

