  AND(
    [Mail Merge Options - Document Selection] <> NULL, 
    HASH_GET([Mail Merge Options - Document Selection], "FileName") <> [Mail Merge Purpose]  
  )
)

FieldEntry.Formula:
LET $numberInRow := $eventargs;
LET $process := IF( $numberInRow > ARRAY_LENGTH([This Stage Process List]), "Tracking", //fallback process
$numberInRow = NULL, ARRAY_LAST([This Stage Process List]),
ISNUMBER($numberInRow) = TRUE, ARRAY_INDEX([This Stage Process List], $numberInRow),
$eventargs );
LET $result := IF( ARRAY_INDEXOF([Visible Steps], $process) > 0, ARRAY_WHERE([Visible Steps], $(i, item) $item <> $process), ARRAY_CONCAT([Visible Steps], $process) );
RETURN IF( [Support Multiple Visible] = TRUE, ARRAY_WHERE( ARRAY_DISTINCT($result), $(i, item) $item <> NULL ), ARRAY($process) )
