Column.Formula:
LET $today := NOW();
LET $day := DAY($today);
LET $month := MONTH($today);

RETURN
//Generate quarterly
OR(
	AND($day = 31, $month = 3),
	AND($day = 30, $month = 6),
	AND($day = 30, $month = 9),
	AND($day = 31, $month = 12)
)

Column.DefaultValueFormula:
LET $targetEForm := "Payment Certificate";

LET $targetData := 
ARRAY(
  "RecordID", 
  "Status", 
  "DateCreated",
  "Contract Number",
  "Vendor Name",
  "Vendor RecordID",
  "Total Net Amount Payable",
  //"Department", 
  //"Cost Center", 
  //"Contract Type", 
  "Date Approved", 
  "Date of Issue"//, 
  //"Approved Variation Value", 
  //"Initial Contract Value Incl. Tax"
);

LET $filter := "[Status] = 'Approved' AND [LineNumber] FAST_EQUAL_TO 1";

LET $data := 
DB_LOOKUPALL(
  $targetData,
  $targetEForm,
  $filter
);

RETURN 
ARRAY_SELECT(
  $data, $(i, item) 
  INDEXES_TO_KEYS(
	$item, 
	$targetData
  )
)

LET $FYfirstHalf := MONTH(NOW()) > 6;

LET $date := 
IF( 
  $FYfirstHalf = TRUE,
  DATE(YEAR(NOW()), 7), 
  DATE(YEAR(NOW()) - 1, 7)
);

RETURN $date

LET $FYfirstHalf := MONTH(NOW()) > 6;

LET $date := 
IF( 
  $FYfirstHalf = TRUE,
  DATE(YEAR(NOW()) + 1, 7), 
  DATE(YEAR(NOW()), 7)
);

RETURN DATEADD("second", -1, $date)

LET $FYfirstHalf := MONTH(NOW()) > 6;

LET $fy := 
IF( 
  $FYfirstHalf = TRUE,
  LEFT(YEAR(NOW()), 2), 
  LEFT(YEAR(NOW()) - 1, 2)
);

LET $value := 
IF( 
  $FYfirstHalf = TRUE,
  YEAR(NOW()), 
  YEAR(NOW()) - 1
);

RETURN 
$value

Column.Formula:
IF(
  [Grouping Period] = "day", 
  	FLOOR([Date To] - [Date From]), 

  [Grouping Period] = "week", 
  	CEILING(([Date To] - [Date From]) / 7), 

  [Grouping Period] = "month", 
  (
	LET $result := DATEDIFF_PARTS([Date From], [Date To]);
    RETURN  ARRAY_INDEX($result, 1) * 12 + ARRAY_INDEX($result, 2) + IF(ARRAY_INDEX($result, 3) > 0, 1)
  ),

  [Grouping Period] = "quarter", 
  (
	LET $result := DATEDIFF_PARTS([Date From], [Date To]);
    RETURN  CEILING((ARRAY_INDEX($result, 1) * 12 + ARRAY_INDEX($result, 2) + IF(ARRAY_INDEX($result, 3) > 0, 1)) / 3)
  ),

  [Grouping Period] = "year", 
  (
	LET $result := DATEDIFF_PARTS([Date From], [Date To]);
    RETURN  CEILING((ARRAY_INDEX($result, 1) * 12 + ARRAY_INDEX($result, 2)) / 12)
  ),

  [Grouping Period] = "alltime", 
	1
)

//----Full Data Set----
LET $data := [Data];

//----Filtered Data Set----
LET $buildTargetData := $(targetDate)
ARRAY_WHERE(
  $data, $(i, item)
  LET $date := HASH_GET($item, $targetDate);
  RETURN 
  AND(
	$date >= [Date From],
    $date <  [Date To]
  )
);

//----These variables must be the same length
LET $seriesNames := 
ARRAY(
  //"Date Approved", 
  "Total Amount Payable"
); 

LET $targetDates := 
ARRAY(
  //"Date Approved", 
  "Date of Issue"
); 

//----Chart Customisation----
LET $split := [Period List];
LET $labels := [Period List]; 



//----Group data based on period----
LET $period := [Grouping Period];

LET $groupedData := 
ARRAY_SELECT(
  $seriesNames, $(seriesNumber, series)

	LET $result := 
    ARRAY_SELECT(
	  $split, $(s, startOfPeriod) 
	  (	
		LET $targetDate := ARRAY_INDEX($targetDates, $seriesNumber);
		LET $targetData := CALL($buildTargetData, $targetDate);

		LET $matches := 
		  ARRAY_WHERE(
			$targetData, $(i, item)
			LET $contextDate := HASH_GET($item, $targetDate);
			LET $periodEnd := 
			IF( 
				$period = "day", 		DATEADD("day", 	1, $startOfPeriod),
				$period = "week", 	 	DATEADD("day", 	7, $startOfPeriod),
				$period = "month",	 	DATEADD("month", 	1, $startOfPeriod),
				$period = "quarter", 	DATEADD("month", 	3, $startOfPeriod),
				$period = "year", 	 	DATEADD("year",	1, $startOfPeriod),
				$period = "alltime", 	[Date To]
			);

			RETURN
			AND(
			  $contextDate >= $startOfPeriod,
			  $contextDate < $periodEnd
			)
		  );

		RETURN $matches		//ARRAY_LENGTH($matches)
		)
	  );
	  RETURN $result//ARRAY_WHERE($result, $(i, item) $item <> ARRAY())
);

RETURN 
$groupedData

