  ARRAY_SELECT(
	[Period List], $(p, period)
	LET $fy := FINANCIALYEAR($period) - 1;
	LET $fyStart := EBMS_GET_FINANCIAL_YEAR_START(DATE($fy + 1)); 
	LET $quarter := 
	IF( 
		$period >= DATEADD("month", 9, $fyStart), 4, 
		$period >= DATEADD("month", 6, $fyStart), 3, 
		$period >= DATEADD("month", 3, $fyStart), 2, 
		1
	);
	RETURN 
	"FY" & RIGHT($fy, 2) & "-" & RIGHT($fy, 2)+1 & " Q" & $quarter

  ), 
  $split
);

LET $seriesData := 
ARRAY_SELECTMANY(
  $seriesNames, $(s, series)
  ARRAY(
	  HASH(
	    "label", $series, 
		"color", ARRAY_INDEX([Theme Colours], $s)
	  )
  )
);

LET $types := 
ARRAY(
	"bar",
	"bar"  
); 

LET $looper := 
ARRAY_SELECTMANY(
	RANGE(0, ARRAY_LENGTH($seriesNames)), 
	$(i, item) 
	[Report Data]
); 

LET $chartData := 
ARRAY_SELECT(
	$looper, $(s ,item) 
	HASH(
		"type", ARRAY_INDEX($types, $i),
		"data", 
	  		ARRAY_SELECT(
				$item, $(i, item) 
				LET $matches := 
				ARRAY_WHERE(
					$item, $(i, item) 
					HASH_GET($item, "Vendor Name") = ARRAY_INDEX($seriesNames, $s)
				);

				RETURN SUM(ARRAY_SELECT($matches, $(i, item) HASH_GET($item, "Total Net Amount Payable")))

			)
	)
);

//----Build chart----
RETURN 
HASH(
    "series", $seriesData,
    "panes", 
    ARRAY(
        HASH(
            "labels", $labels,
			"layers", $chartData
        )
    )
)

