LET $seriesNames := 
ARRAY(
  "Certificates Issued", 
  "Value of Certificates"
); 

LET $targetDates := 
ARRAY(
  "Date of Issue", 
  "Date of Issue"
); 

LET $tableData := 
ARRAY_SELECT(
	ARRAY_CONCAT([Report Data], [Report Data]), $(s, series) 

	LET $rowData := 
  	ARRAY_SELECT(
		$series, $(r, set)

		LET $value := 
	    IF(
		  ARRAY_INDEX($seriesNames, $s) = "Value of Certificates", 
		  NUMBERFORMAT(SUM(ARRAY_SELECT($set, $(i, item) HASH_GET($item, "Total Net Amount Payable"))), "C"), 
	      ARRAY_LENGTH($set)
	    );

		LET $startOfPeriod := ARRAY_INDEX($split, $r); 
		LET $targetDate := ARRAY_INDEX($targetDates, $s);
		LET $endOfPeriod :=
		IF( 
			$period = "day", 		 DATEADD("day", 	1, $startOfPeriod),
			$period = "week", 	 	DATEADD("day", 	7, $startOfPeriod),
			$period = "month",	 DATEADD("month", 	1, $startOfPeriod),
			$period = "quarter", 	 DATEADD("month", 	3, $startOfPeriod),
			$period = "year", 	 	DATEADD("year",	1, $startOfPeriod),
		    $period = "alltime", 	 	[Date To]
		);

		LET $urlFilter := DB_ENCODEIDENTIFIER($targetDate) & " >= " & N($startOfPeriod) & " AND " & DB_ENCODEIDENTIFIER($targetDate) & " < " & N($endOfPeriod) & " AND [Status] = 'Approved'";
		LET $url := "tabularreport/Payment%20Certificate?profile=Details&where=" & URLENCODE($urlFilter); 

		RETURN
		HASH(
			"value", $value, 
			"url", $url
		)
	);

  RETURN 
  ARRAY_CONCAT(
	ARRAY(HASH("value", ARRAY_INDEX($seriesNames, $s))), 
	$rowData
  )
);

LET $table := 
HASH(
	"columns", $tableColumns,
	"rows", $tableData
);

RETURN 
$table

