			$period = "quarter", 	 DATEADD("month", 	3, $startOfPeriod),
			$period = "year", 	 	DATEADD("year",	1, $startOfPeriod),
		    $period = "alltime", 	 	[Date To]
		);

		LET $urlFilter := DB_ENCODEIDENTIFIER($targetDate) & " >= " & N($startOfPeriod) & " AND " & DB_ENCODEIDENTIFIER($targetDate) & " < " & N($endOfPeriod) & " AND [Status] = 'Approved'";
		LET $url := "tabularreport/Purchase%20Order?profile=Details&where=" & URLENCODE($urlFilter); 

		RETURN
		HASH(
			"value", $value, 
			"url", $url
		)
	);

  RETURN 
  ARRAY_CONCAT(
	ARRAY(HASH("value", ARRAY_INDEX($seriesNames, $s))), 
	$rowData
  )
);

LET $table := 
HASH(
	"columns", $tableColumns,
	"rows", $tableData
);

RETURN 
$table

LET $earliestYear := 

GREATEST(
  YEAR(LEAST(ARRAY_SELECT(
  	[Data], $(i, item) 
  	HASH_GET($item, "Start Date")
  ))), 
  2015
);

LET $latestYear := 
YEAR(GREATEST(ARRAY_SELECT(
  [Data], $(i, item) 
  HASH_GET($item, "Current End Date")
)));

LET $yearsCovered := $latestYear - $earliestYear - 1;

LET $list := 
ARRAY_SELECT(
	RANGE($earliestYear, $yearsCovered), $(i, item)
	LET $year := YEAR(NOW()) + $i;
	LET $text := RIGHT($year, 2) & "/" & N(RIGHT($year, 2)) + 1;
	RETURN 
	HASH(
	  "text", $text, 
	  "value", $year
	)		  
);

RETURN 
ARRAY_CONCAT(
  ARRAY(HASH("text", "All", "value", 0)),
  $list
)

