FieldEntry.Formula:
LET $targetEForm := "Payment Certificate";

LET $targetData := 
ARRAY(
  "RecordID", 
  "Status", 
  "DateCreated",
  "Contract Number",
  "Vendor Name",
  "Vendor RecordID",
  "Total Net Amount Payable",
  //"Department", 
  //"Cost Center", 
  //"Contract Type", 
  "Date Approved", 
  "Date of Issue"//, 
  //"Approved Variation Value", 
  //"Initial Contract Value Incl. Tax"
);

LET $filter := "[Status] = 'Approved' AND [LineNumber] FAST_EQUAL_TO 1 AND [Total Net Amount Payable] > 0 AND (" & ([Vendor Name] = NULL()) & " OR [Vendor Name] = " & DB_ENCODECONSTANT([Vendor Name]) & ")";

LET $data := 
DB_LOOKUPALL(
  $targetData,
  $targetEForm,
  $filter
);

RETURN 
ARRAY_SELECT(
  $data, $(i, item) 
  INDEXES_TO_KEYS(
	$item, 
	$targetData
  )
)

