TriggerCustomEvent.EventArgumentsFormula:
//finds the highest linenumber on the record and runs that
LET $result := 
DB_REPORT(
  "MAX([LineNumber])", 
  "Award", 
  HASH(
    "RecordID", ARRAY_FIRST([Award RecordID])
  )
);

RETURN 
HASH("LineNumber", ARRAY_FIRST(ARRAY_FIRST($result)))

