FormulaCriterion.Formula:
//Update the evaluation if this is the last response to be sent across
LET $numberOfResponses := 
ARRAY_FIRST(
  ARRAY_FIRST(
	DB_REPORT(
	  "COUNT([RecordID])", 
	  "VP Response", 
	  HASH(
		"LineNumber", 1, 
		"Evaluation Opportunity Id", [Evaluation Opportunity Id]
      )
    )
  )
);

RETURN 
  //required because when initially syncing the final response isn't saved until after this task would hit, so the totally saved records is minus one
  $numberOfResponses = (HASH_GET([Opportunity Details], "responseCount") - 1)

//Update the evaluation if this is the last response to be sent across
LET $numberOfResponses := 
ARRAY_FIRST(
  ARRAY_FIRST(
	DB_REPORT(
	  "COUNT([RecordID])", 
	  "VP Response", 
	  HASH(
		"LineNumber", 1, 
		"Evaluation Opportunity Id", [Evaluation Opportunity Id]
      )
    )
  )
);

RETURN 
$numberOfResponses = HASH_GET([Opportunity Details], "responseCount")
