FieldEntry.Formula:
LET $validationData := ARRAY( HASH("Error ID", "Start Date", "Message", IF([Temp End Date Before Exercised] == NULL, "Please ensure contract has a valid end date before raising variation to end date")), HASH("Error ID", "AutoExtend", "Message", IF([Temp AutoExtend] == NULL, "Please specify whether this extension should be applied on variation approval")), HASH("Error ID", "End Date Mode", "Message", IF([End Date Mode] == NULL, "Please specify a duration for this extension")), HASH("Error ID", "Duration", "Message", IF([Temp End Date After Exercised] <= [Temp End Date Before Exercised], "Please specify a valid duration for this extension")) );
RETURN
ARRAY_WHERE($validationData, $(i,item) HASH_GET($item,"Message") <> NULL)

FormulaDrivenChangeFieldValues.FieldValuesFormula:
LET $columns:=
ARRAY(
  	"End Date Before Exercised",
	"AutoExtend",
  	"Extension Years",
  	"Extension Months",
  	"Extension Days",
  	"End Date After Exercised"
  );
LET $existing := 
ARRAY_TRANSPOSE(
	ARRAY_SELECT(
		$columns, $(c, col)
	  	CURRENT_EACHVALUE($col)
	)
);

LET $newSet := 
ARRAY(
    [Temp End Date Before Exercised],
	[Temp AutoExtend],
  	[Temp Extension Years],
  	[Temp Extension Months],
  	[Temp Extension Days],
  	[Temp End Date After Exercised]
);

LET $combined := 
ARRAY_CONCAT(
	$existing, 
	ARRAY($newSet)
);

LET $filtered := 
ARRAY_WHERE(
	$combined, $(i, item) 
	ARRAY_INDEX($item, 1) <> NULL()
);

LET $result := ARRAY_DISTINCT($filtered);

RETURN 
INDEXES_TO_KEYS(
  ARRAY_TRANSPOSE($result),
  $columns
)

