Binding.Expression:
LET $years := [Extension Years];
LET $months := [Extension Months];
LET $days := [Extension Days];

RETURN

IF($years > 0, $years & IF($years > 1, " years", " year")) &
IF($months > 0, IF($years > 0, ", ") & $months & IF($months > 1, " months", " month")) &
IF($days > 0, IF(OR($years > 0, $months > 0), ", ") & $days & IF($days > 1, " days", " day"))

LET $tick := 
"<span><svg viewBox=""0 0 512 512"" class=""svg-inline--fa fa-check fa-w-16"" data-icon=""check"" data-prefix=""fas"" aria-hidden=""true"" xmlns=""http://www.w3.org/2000/svg"" style=""fill: #0d851e; font-size: 13px;""><path d=""M173.898 439.404l-166.4-166.4c-9.997-9.997-9.997-26.206 0-36.204l36.203-36.204c9.997-9.998 26.207-9.998 36.204 0L192 312.69 432.095 72.596c9.997-9.997 26.207-9.997 36.204 0l36.203 36.204c9.997 9.997 9.997 26.206 0 36.204l-294.4 294.401c-9.998 9.997-26.207 9.997-36.204-.001z"" fill=""""></path></svg></span>";

