TriggerCustomEvent.EventArgumentsFormula:
LET $targetType := "Variation";
LET $mostRecentSubmission := DB_LOOKUP( ARRAY("RecordID", "Round"), "Submission", "[Type] = " & DB_ENCODECONSTANT($targetType), HASH("sort", "[Round] DESC", "ancestor", ARRAY(EFormType, RecordID)) );
RETURN HASH( "Submission Type", $targetType, "Previous Submission Round", COALESCE(ARRAY_LAST($mostRecentSubmission), 0), "Previous Submission RecordID", ARRAY_FIRST($mostRecentSubmission), "Parent Record", ARRAY([EFormType], [RecordID]), "Review Record", ARRAY([EFormType], [RecordID]), "Submitter Notes", NULL )

FieldEntry.Formula:
LET $columns := ARRAY( "Schedule of Rates RecordID", "Original End Date", "Current End Date", "Vendor Access Enabled", "Enabled Vendor Actions" );
LET $data := DB_LOOKUP( $columns, "Contract", HASH("RecordID", [Contract RecordID]) );
RETURN INDEXES_TO_KEYS($data, $columns)

LET $hash := HASH("parent", ARRAY("Contract", [Contract RecordID]));
LET $count := ARRAY_FIRST( ARRAY_FIRST( DB_REPORT( "COUNT([RecordID])", "Variation", "[LineNumber] = 1" & " AND [DateCreated] < " & DB_ENCODECONSTANT([DateCreated]), $hash ) )) + 1;
RETURN IFERROR($count, 1)

LET $columns := 
ARRAY(
  "Variation Value", 
  "Tax", 
  "Status"
);

LET $specialFilter := HASH("parent", ARRAY("Contract", [Contract RecordID]));

LET $filter := "[LineNumber] = 1 AND [DateCreated] < " & DB_ENCODECONSTANT([DateCreated]);

LET $data := 
DB_LOOKUPALL(
	$columns, 
	"Variation", 
	$filter,
	$specialFilter
);

LET $itemsHash :=  
ARRAY_SELECT(
  $data, $(i, item)
  INDEXES_TO_KEYS(
    $item, 
    $columns
  )
);

LET $totalsNames := 
ARRAY(
  "Total Approved Variation Value", 
  "Total Approved Tax",
  "Count of Approved Variations",

  "Total Potential Variation Value",
  "Total Potential Tax", 
  "Count of Potential Variations"
); 

LET $totals :=  
ARRAY_SELECT(
  $totalsNames, $(i, item) 

  LET $checkStatus := $(item, statusCheck)(

    LET $status := HASH_GET($item, "Status");
	LET $approved := $status = "Approved";
	LET $notApproved := AND($status <> "Rejected", $status <> "Cancelled", $status <> "Approved");
	LET $potential := AND($status <> "Rejected", $status <> "Cancelled");
	RETURN 
	IF( 
		$statusCheck = "Approved", $approved, 
		$statusCheck = "Potential", $potential, 
		$statusCheck = "NotApproved", $notApproved, 
	)	
  ); 

  RETURN 
  IF( 
	//approved
	$item = "Total Approved Variation Value", 
	SUM(ARRAY_SELECT(ARRAY_WHERE($itemsHash, $(i, item) CALL($checkStatus, $item, "Approved")), $(i, item) HASH_GET($item, "Variation Value"))),

	$item = "Total Approved Tax", 
	SUM(ARRAY_SELECT(ARRAY_WHERE($itemsHash, $(i, item) CALL($checkStatus, $item, "Approved")), $(i, item) HASH_GET($item, "Tax"))),

	$item = "Count of Approved Variations", 
	ARRAY_LENGTH(ARRAY_WHERE($itemsHash, $(i, item) CALL($checkStatus, $item, "Approved"))),

	//potential
	$item = "Total Potential Variation Value", 
	SUM(ARRAY_SELECT(ARRAY_WHERE($itemsHash, $(i, item) CALL($checkStatus, $item, "Potential")), $(i, item) HASH_GET($item, "Variation Value"))),

	$item = "Total Potential Tax", 
	SUM(ARRAY_SELECT(ARRAY_WHERE($itemsHash, $(i, item) CALL($checkStatus, $item, "Potential")), $(i, item) HASH_GET($item, "Tax"))),

	$item = "Count of Potential Variations", 
	ARRAY_LENGTH(ARRAY_WHERE($itemsHash, $(i, item) CALL($checkStatus, $item, "Potential")))
  )
);

LET $totalsHash := 
INDEXES_TO_KEYS(
  $totals, 
  $totalsNames
);

RETURN 
$totalsHash

LET $status := ARRAY_INDEX(HASH_GET([Extension Data], "Status"), $count);

RETURN 
IF( 
	$status = "Exercised",
    NOW()
)

TriggerCustomEvent.TargetRecordsFormula:
LET $approvedExtensions :=
ARRAY_WHERE(
  [Temp], $(i, item)
  DB_LOOKUP("Status", "Extension", "[Status] = 'Exercised' AND [RecordID] = " & DB_ENCODECONSTANT($item)) <> NULL()
);

RETURN 
ARRAY_SELECT(
  $approvedExtensions, $(i, item) 
  ARRAY("Extension", $item)
)

FieldEntry.Formula:
LET $refreshFormulas := HASH("Temp", ""); 

LET $contractUpdate := 
ARRAY_SELECT(
	[Contract RecordIDs], $(i, item)
	ARRAY(
		ARRAY("Contract", $item),
		$refreshFormulas
	)
);

LET $thisRecordUpdate := 
ARRAY(
	ARRAY(
		HASH_GET($eventargs, "Parent Record"),
		HASH_MERGE(
			HASH("Variation Approved", TRUE),
			HASH("Status", "Approved")
		)
	)
);

RETURN
ARRAY_CONCAT(
	$thisRecordUpdate,
	$contractUpdate 
)

IF( 
	[Variation to Date] == TRUE, 
	ARRAY_SELECTMANY(
		EACH [Count of Extensions], $(i, item) 
		ARRAY_SELECT(
			RANGE(0, $item), $(e, ext)
			ARRAY("Contract", [Vary Contract RecordID]#$i)	
		)
	)
)

Navigate.UrlTemplate:
{
LET $recordID := 
DB_LOOKUP(
	"RecordID",
	"Submission",
	"[Status] IN ('Draft', 'Submitted') ",
	HASH("parent", ARRAY(EFormType, RecordID))
);

RETURN EBMS_GET_EFORMRECORD_URL("Submission", $recordID)
}

FormulaDrivenRaiseLinkedEForm.FieldValuesFormula:
LET $cols := ARRAY( //"Variation to Date", //"Variation to Price", "Variation to Terms",
"Variation Title", "Reason for Variation", "Details", "Variation Reason", "Time Impact", "Risk", "Recommendation", "Term Variation Reference", "Term Variation Details" );
LET $values:= ARRAY_SELECT( $cols, $(c, col) CURRENT_EACHVALUE($col) );
RETURN HASH_MERGE( INDEXES_TO_KEYS($values, $cols), HASH("Source Variation RecordID", [RecordID]), HASH("Status", "Submitted") )

