FieldEntry.Formula:
LET $data := 
(
	ARRAY_SELECT(
		[Contract RecordIDs], $(i, item)
		IF( 
			[Variation to Price] = TRUE,
			HASH(
				"Vary Contract RecordID", [Vary Contract RecordID]#$i,
				"Variation Number", [Variation Number],
				"Variation Value", [Variation Value]#$i,
				"Reason for Variation", [Reason for Variation]
			), 
			NULL()
		)
	)
);

LET $scheduleRecordIDs := 
ARRAY_SELECT(
	$data, $(i, item) 
	LET $recordID := HASH_GET($item, "Vary Contract RecordID");
	RETURN 
	DB_LOOKUP(
		"RecordID",
		"Schedule of Rates", 
		"[Contract RecordID] = " & DB_ENCODECONSTANT($recordID)
	)
);

LET $cleandata := 
ARRAY_WHERE(
	$data, $(i, item) 
	ARRAY_INDEX($scheduleRecordIDs, $i) <> NULL()
);

LET $cleanList := 
ARRAY_WHERE(
	$scheduleRecordIDs, $(i, item) 
	$item <> NULL()
);

RETURN 
IF( 
	$scheduleRecordIDs <> ARRAY(NULL()),

	HASH(
		"recordIDs", $cleanList, 
		"data", $cleandata
	)
)

