LET $validationData :=

ARRAY(
  	IF([Variation Title] == NULL, HASH("Error ID", "Title", "Message", "Please specify a title for this variation")),
  	IF([Details] == NULL, HASH("Error ID", "Details", "Message", "Please provide a summary of this variation")),
  	IF([Reason for Variation] == NULL, HASH("Error ID", "Reason", "Message", "Please select a variation reason")),
  	IF(AND([Variation to Date] <> TRUE, [Variation to Price] <> TRUE, [Variation to Terms] <> TRUE), HASH("Error ID", "Type", "Message", "Please select at least one variation type")),

  	//Variation to Price
  	IF(AND([Variation to Price] == TRUE, [Variation Value] = 0), HASH("Error ID", "Value", "Message", "Please specify the value of this variation")),

  	//Variation to Terms
  	IF(AND([Variation to Terms] == TRUE, ARRAY_LENGTH(ARRAY_WHERE(EACH [Term Variation Details], $(i,item) $item = "")) > 0), HASH("Error ID", "Terms", "Message", "Please provide details for each variation to terms")),

  	//Variation to Date
  	IF(AND([Variation to Date] == TRUE, [Count of Extensions] <= 0), HASH("Error ID", "Date", "Message", "Please add at least one extension option for this variation"))
);

RETURN

ARRAY_WHERE($validationData, $(i,item) $item <> NULL)
