Column.Formula:
LET $cleanData := 
ARRAY_WHERE(
	[Notes], $(i, item) 
	ARRAY_INDEX($item, 3) <> NULL()	// Ignore items with no message to display
);

LET $cleanData :=
	IF([Newest At Top] == TRUE,
	  	ARRAY_REVERSE($cleanData),
	  	$cleanData
	);



LET $formatted := 
ARRAY_SELECT(
	$cleanData, $(i, item) 
	LET $user := ARRAY_INDEX($item, 1); 
	LET $datePosted := ARRAY_INDEX($item, 2); 
	LET $message := IFERROR(ARRAY_INDEX($item, 3), "");
	LET $attachments := ARRAY_INDEX($item, 4);


	LET $isSystem := 
  		OR(
		  	ROLE("system") == $user,
		  	ISBLANK($user)				// Usefulf or wedge import and exports when the user no longer matches
		);
	LET $isCurrentUser := EBMS_GET_CURRENT_USER() == $user;
	LET $userDisplayName := 
  		IF(
		  	$isSystem = TRUE, 
		  	"System", 
		  	//ELSE
		  	COALESCE(
			  	EBMS_GET_ROLE_DISPLAYNAME($user),
				$user
		  	)
		); 
	LET $datePostedFormatted := DATEFORMAT($datePosted, "dd-MMM-yyyy hh:mm tt");
	LET $class := IF($isSystem, "system", $isCurrentUser, "right-top", "left-top");
	LET $colour := IF($isSystem, "#000000", $isCurrentUser, "#00497b" , "#347016");

	LET $attachmentOutput := 
	IF( 
		ARRAY_LENGTH($attachments) > 0,
		(
		LET $attachmentList := 
			ARRAY_JOIN(ARRAY_SELECT(
				$attachments, $(i, item) 
				LET $filenameEncoded := HTMLENCODE($item); 
				LET $path := HTMLENCODE("GetEFormRecordFile.ashx?EFormType=" & [EFormType] & "&RecordID=" & [RecordID] & "&FileName=" & URLENCODE($item) & "&AsAttachment=true&Path=Chat Attachments");

				RETURN
				"<div style=""margin-bottom: 3px""><a href=""" & $path & """>" & $filenameEncoded & "</a></div>"
			), "");

		RETURN		  
		  "<div style=""border: 1px solid #ccc; margin: 5px; margin-bottom: 10px;text-align: center; background: white;"">" &
		      "<div style=""margin-bottom: 3px; background: #cccccc; padding: 2px;"">Attachments</div>" & 
  				$attachmentList & 
		  "</div>"	  
		)
	);

    LET $systemMessage := 
    "<div style=""text-align: center;"" class=""systemChatDisplay " & $class & """>" &     	
	"<span class=""nimblexCOREChatMessageContainer"" style=""text-align: center;"">" & $message & "</span>" & 
  	"<span style=""float:right;""> " & $datePostedFormatted & "</span>" &
	"</div>";

    LET $chatMessage := 
	"<div class=""talk-bubble tri-right " & $class & """>" &   
	"<span style=""font-weight: 600; color: " & $colour & """>" & HTMLENCODE($userDisplayName) & "</span>" & 
	"<span style=""font-style: italic;""> " & $datePostedFormatted & "</span>:" & 
	"<span class=""nimblexCOREChatMessageContainer"" style=""text-align: left; word-wrap: break-word;"">" & $message & "</span>" & 
	$attachmentOutput &
	"</div>";

	RETURN 
	IF($isSystem, $systemMessage, $chatMessage)
);

