LET $combined := ARRAY_CONCAT($originator, $specificPeople);

LET $emails := ARRAY_SELECT($combined, $(i, item) EBMS_GET_ROLE_EMAIL_ADDRESSES($item));

LET $array := ARRAY_DISTINCT(ARRAY_WHERE($emails, $(i, item) $item <> NULL()));

