ARRAY_JOIN($array, ", ")

// This field is for showing in a tabular report column and simulating new lines.
// Issue: Tabular reports does not render new lines
// Fact 1: Lines without a breaking charater will just flow of screen. If a collection non-breaking characters are used with a space on either side. This non-breaking string will popuate an entire line.
// Work around: Make sure that the first line is non breaking with a large white space afterwards to force a new line of its own. Apend the rest of the message after a breaking space.
LET $startOfNewLineSymbol := "•";
LET $regexWhiteSpace := "\s";

LET $nonBreakingWhiteSpace := " "; // This uses figure space. This special space is a non breaking white space. 
LET $breakingWhiteSpace := " ";

LET $nonBreakingDash := "‑";
LET $breakingDash := "-";

LET $longNonBreakingWhiteSpace := "                                                                                                                          "; // This uses figure space. Figure space is a non breaking white space. 
LET $startOfMessageSeparatorLine := "___________________________________________________________________________________________________________________________";

// Lets get started
LET $cleanData := 
ARRAY_WHERE(
	[Notes], $(i, item) 
	ARRAY_INDEX($item, 3) <> NULL()
);

LET $arrayOfStings := 
ARRAY_SELECT(
  	// Get all the data
	$cleanData, $(i, item) 
	LET $user := ARRAY_INDEX($item, 1); 
	LET $datePosted := ARRAY_INDEX($item, 2); 
	LET $message := IFERROR(ARRAY_INDEX($item, 3), "");
	LET $attachments := ARRAY_INDEX($item, 4);

  	// Clean the Data
  	LET $isSystem := ROLE("System") == $user;
  	LET $userDisplayName := IF($isSystem = TRUE, "System", EBMS_GET_ROLE_DISPLAYNAME($user)); 
	LET $datePostedFormatted := DATEFORMAT($datePosted, "dd‑MMM‑yyyy hh:mm tt");

	// Remove HTML formatting
  	LET $messageCleaned := $message;
  	LET $messageCleaned := REGEX_REPLACE($messageCleaned,"\<.*?\>", $(i) "");		// Remove HTML tags like <div></div> or <b></b>
  	LET $messageCleaned := REGEX_REPLACE($messageCleaned,"\\n", $(i) "");			// Remove new line character
  	LET $messageCleaned := REPLACESUBSTRING("&#39;", $messageCleaned, "'");			// Replace commas 
  	LET $messageCleaned := REPLACESUBSTRING("&quot;", $messageCleaned,"""");
  	LET $messageCleaned := REPLACESUBSTRING("&nbsp;", $messageCleaned,, " ");

  	LET $compiledStr := $startOfNewLineSymbol & $datePostedFormatted & " " & $userDisplayName & ": " & $messageCleaned & $breakingWhiteSpace;

  	// Find the last white space break on the first line.
  	LET $breakStringBefore := 87;	// Based on column width
  	LET $safeBreakPoint := FINDR($breakingWhiteSpace, LEFT($compiledStr, $breakStringBefore - 1));

  	// Replace all breaking characters from Start of string
  	LET $nonBreakdingStart := LEFT($compiledStr, $safeBreakPoint - 1);
  	LET $nonBreakdingStart := REPLACESUBSTRING($breakingDash, $nonBreakdingStart, $nonBreakingDash);
  	LET $nonBreakdingStart := REGEX_REPLACE($nonBreakdingStart, $regexWhiteSpace, $(i)$nonBreakingWhiteSpace);

  	// Attach non-breaking white spaces to forece a new line for the first line of every message
  	LET $nonBreakdingStart := $nonBreakdingStart & $longNonBreakingWhiteSpace;

  	// Reattach Start
  	LET $compiledStr := $nonBreakdingStart & $breakingWhiteSpace & MID($compiledStr, $safeBreakPoint, LEN($compiledStr));

  	// Add a break to the very end
  	LET $compiledStr := $compiledStr & $breakingWhiteSpace;

  	RETURN
  	$compiledStr
);

LET $oneLongStr := IFERROR(
	ARRAY_JOIN(
		$arrayOfStings, ""
	), 
	NULL()
);

RETURN
$startOfMessageSeparatorLine & " " & $oneLongStr

LET $messageCleaned := [New Message];
//LET $messageCleaned := REGEX_REPLACE($messageCleaned,"\<.*?\>", $(i) "");		// Remove HTML tags like <div></div> or <b></b>
//LET $messageCleaned := REGEX_REPLACE($messageCleaned,"\\n", $(i) "");			// Remove new line character
//LET $messageCleaned := REPLACESUBSTRING("&#39;", 	$messageCleaned, 	"'");			// Replace commas 
//LET $messageCleaned := REPLACESUBSTRING("&quot;", 	$messageCleaned,	"""");
//LET $messageCleaned := REPLACESUBSTRING("&nbsp;", 	$messageCleaned,	"");
//LET $messageCleaned := REPLACESUBSTRING("n", 		$messageCleaned, 	"");

RETURN
AND(
	LEN([New Message]) <= 1000,							// For catching HTML elements that are striped in a normal check
  	LEN(STRIPXML([New Message])) <= 1					// Must be greater the 1. As sometimes 1 is also empty. Dont know why
)
//ISBLANK($messageCleaned) & ":" & $messageCleaned
//OR(
// 	[New Message] == "<p></p>",				// Value on new records
//  	LEN(STRIPXML([New Message])) == 1		// Value after the user clears the box
//)

LET $current := ARRAY([EFormType], [RecordID]);
LET $descriptionColumns := 
HASH(
	"Contract",  					"Contract Number", 
	"Vendor", 						"Company Name",
	"Procurement", 					"Procurement Number",
	"Project", 						"Project Number",
    "Evaluation", 		    		"Description",
  	"Information Request",  		"File No.", 
	"Subpoena", 					"File No.",
	"Privacy Incident", 			"File No."
); 

LET $altFormName:= HASH(
  	"Insurance",	"Compliance"
  );

LET $getLabelText := $(eFormType, recordID)
(
  	LET $targetColumn := HASH_GET($descriptionColumns, $eFormType);
  	RETURN 
  	IF( $targetColumn = NULL, NULL,
		LET $isthisRecord := ARRAY($eFormType, $recordID) = $current;
		LET $title:= 
		IF( $isthisRecord = TRUE, 
			CURRENT_VALUE($targetColumn),
			ARRAY_FIRST(DB_LOOKUP(
				ARRAY($targetColumn),
				$eFormType, 
				"[RecordID] FAST_EQUAL_TO " & $recordID
			))
		  );
		//shortern the descriptive label/ title
		LET $charCount := LEN($title);
		LET $titleArray := SPLIT($title, " ");
		LET $wordCount := ARRAY_LENGTH($titleArray);

		LET $charLimit := 30; 
		LET $wordLimit := ARRAY_LENGTH( SPLIT( LEFT($title, $charLimit), " ") );

	  RETURN //ARRAY_TAKE($titleArray, $wordLimit)
		IF(
			$wordCount <= $wordLimit, $title,
			ARRAY_JOIN(
				ARRAY_TAKE($titleArray, $wordLimit), " "
			)& " ..."
		)
	  )

); 

LET $getReadAccess := $(eFormType, recordID)
( EBMS_GET_EFORMRECORD_PERMISSION($eFormType, $recordID, "read"));

LET $currentDes := CALL($getLabelText, [EFormType], [RecordID]);
LET $currentAccess := FALSE;

RETURN
IF(
	[Parent Structure] <> NULL, [Parent Structure],
  	(

	  LET $parent := 
	  IF( 
		[RecordID] > 0, 
		(
		  LET $record := EBMS_GET_PARENT_RECORD(ARRAY_FIRST($current), ARRAY_INDEX($current, 2));
		  RETURN 
			  IF(  ARRAY_FIRST($record) == NULL, NULL,
				ARRAY_CONCAT( 
				  $record, 
				  CALL($getLabelText, ARRAY_FIRST($record), ARRAY_INDEX($record, 2)),
				  CALL($getReadAccess, ARRAY_FIRST($record), ARRAY_INDEX($record, 2))
				  )

			  )
	  )); 

	  LET $gParent := 
	  IF( 
		 $parent <> NULL, 
		(
		  LET $record := EBMS_GET_PARENT_RECORD(ARRAY_FIRST($parent), ARRAY_INDEX($parent, 2));
		  RETURN 
			  IF(  ARRAY_FIRST($record) == NULL, NULL,
				ARRAY_CONCAT( 
				  $record, 
				  CALL($getLabelText, ARRAY_FIRST($record), ARRAY_INDEX($record, 2)),
				  CALL($getReadAccess, ARRAY_FIRST($record), ARRAY_INDEX($record, 2))
			  )
		  )
	  )); 

	  LET $hash :=  
	  HASH_MERGE(
		  IF( $parent <> NULL, HASH("parent", 	$parent)),
		  IF( $gParent <> NULL, HASH("gparent", 	$gParent))
	  );

	  LET $parentStructure := 	//2nd instance
	  IF( 
		  ARRAY_LENGTH(HASH_KEYS($hash)) >0, $hash, NULL
	  );

	  LET $parentData:= 
	  ARRAY_WHERE(
		ARRAY(
			$gParent, 
			$parent, 
			ARRAY_CONCAT($current, $currentDes, $currentAccess),
		), $(i,item) $item <> NULL
	   );


	  LET $parentChain:=
		ARRAY_SELECT(
			  $parentData, $(i, item)
			  ARRAY_TAKE($item, 2)
	   );


	  LET $parentLinks :=
	  ARRAY_SELECT(
		  $parentData, $(i, item) 
			  LET $eFormType 		:= ARRAY_FIRST($item);
			  LET $recordID 		:= ARRAY_INDEX($item, 2);
			  LET $descriptiveLabel := ARRAY_INDEX($item, 3);
			  LET $hasReadAccess	:= ARRAY_INDEX($item, 4);
			  LET $URL	:= "records/" & URLENCODE($eFormType) & "/" & $recordID ;
			  LET $showEFormType := $eFormType <> "Vendor";

			  LET $labelColour := 
				  IF(
				   AND($eFormType = [EFormType], $recordID = [RecordID]),
					  "background: #ffffff; color: #5D5D5D;",
					 $hasReadAccess = TRUE,
					  "background: #ffffff; color: #4D87B7;",
					  "background: #ffffff; color: #5D5D5D;"

				  );



			  LET $labeltext:= 
					  IF( $showEFormType == TRUE, " " & COALESCE(HASH_GET($altFormName, $eFormType), $eFormType) ) &
					  IF(NOT(ISBLANK($descriptiveLabel)), " " & $descriptiveLabel);

			  LET $label :=  
				  "<span style=""margin-left: 5px;" & $labelColour & """>" &
				  HTMLENCODE($labeltext) & 
				  "</span>";

			  RETURN 
			  IF( 
				  $hasReadAccess == TRUE, 
				  "<a style=""text-decoration-line: none;"" href=" & HTMLENCODE($URL) & ">" & $label & "</a>",
				  $label 
			  )
	  );

	   LET $separator :=   "<svg style=""vertical-align: bottom; margin-left: 5px; height: 18px;"" xmlns=""http://www.w3.org/2000/svg"" viewBox=""0 0 256 512""><path fill=""#5D5D5D"" d=""M246.6 278.6c12.5-12.5 12.5-32.8 0-45.3l-128-128c-9.2-9.2-22.9-11.9-34.9-6.9s-19.8 16.6-19.8 29.6l0 256c0 12.9 7.8 24.6 19.8 29.6s25.7 2.2 34.9-6.9l128-128z""/></svg>";

	  LET $breadcrumb := "<div style=""height: 23px; padding-left: 5px; font-size: 10pt; color: #5D5D5D; "">You are here:" & ARRAY_JOIN($parentLinks, $separator) & "</div>";

	  RETURN 
	  HASH(
		  "Parent Structure",	$parentStructure,
		  "Parent Data", 		$parentData,
		  "Parent Chain",		$parentChain,
		  "Breadcrumb",		$breadcrumb
		)
	 )
)

