LET $emailTo := EBMS_GET_ROLE_EMAIL_ADDRESSES([Assignee], true);
LET $CC := "";
LET $BCC := "";
LET $identifier := IF([Converted to Feature] = TRUE, "Feature " & [Feature ID], "Feedback " & [RecordID]);
LET $emailSubject := "Please review " & $identifier & " at " & ARRAY_LAST(SPLIT(EBMS_GET_BASE_URL(), "/"));

LET $messageCleaned := [New Message];
LET $messageCleaned := REGEX_REPLACE($messageCleaned,"\<.*?\>", $(i) "");		// Remove HTML tags like <div></div> or <b></b>
LET $messageCleaned := REGEX_REPLACE($messageCleaned,"\\n", $(i) "");			// Remove new line character
LET $messageCleaned := REPLACESUBSTRING("&#39;", $messageCleaned, "'");			// Replace commas 
LET $messageCleaned := REPLACESUBSTRING("&quot;", $messageCleaned,"""");
LET $messageCleaned := REPLACESUBSTRING("&nbsp;", $messageCleaned,, " ");
LET $messageCleaned := REGEX_REPLACE($messageCleaned,"^[\r\n]+|\.|[\r\n]+$", $(i) "");			// Remove leading and trailing new lines

LET $messageCleaned := IF([Is New Message Blank] == TRUE, "", NEWLINE() & "❝" & $messageCleaned & "❞" & NEWLINE());

LET $emailBody := "Hi " & EBMS_GET_ROLE_DISPLAYNAME([Assignee]) & ", " &
NEWLINE() &
NEWLINE() & "I have updated " & $identifier & 
