Navigate.UrlTemplate:
{//↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓
// Change these values for your email link.
LET $emailTo := EBMS_GET_ROLE_EMAIL_ADDRESSES([Assignee], true);
LET $CC := "";
LET $BCC := "";
LET $identifier := IF([Converted to Feature] = TRUE, "Feature " & [Feature ID], "Feedback " & [RecordID]);
LET $emailSubject := "Please review " & $identifier & " at " & ARRAY_LAST(SPLIT(EBMS_GET_BASE_URL(), "/"));

LET $messageCleaned := [New Message];
LET $messageCleaned := REGEX_REPLACE($messageCleaned,"\<.*?\>", $(i) "");		// Remove HTML tags like <div></div> or <b></b>
LET $messageCleaned := REGEX_REPLACE($messageCleaned,"\\n", $(i) "");			// Remove new line character
LET $messageCleaned := REPLACESUBSTRING("&#39;", $messageCleaned, "'");			// Replace commas 
LET $messageCleaned := REPLACESUBSTRING("&quot;", $messageCleaned,"""");
LET $messageCleaned := REPLACESUBSTRING("&nbsp;", $messageCleaned,, " ");
LET $messageCleaned := REGEX_REPLACE($messageCleaned,"^[\r\n]+|\.|[\r\n]+$", $(i) "");			// Remove leading and trailing new lines

LET $messageCleaned := IF([Is New Message Blank] == TRUE, "", NEWLINE() & "❝" & $messageCleaned & "❞" & NEWLINE());

LET $emailBody := "Hi " & EBMS_GET_ROLE_DISPLAYNAME([Assignee]) & ", " &
NEWLINE() &
NEWLINE() & "I have updated " & $identifier & 
NEWLINE() & "Please log in and review here: " & EBMS_GET_EFORMRECORD_URL([EFormType], [RecordID]) &
NEWLINE() & $messageCleaned &
NEWLINE() & "Kind Regards, " &
NEWLINE() & EBMS_GET_ROLE_DISPLAYNAME(EBMS_GET_CURRENT_USER());

// This can appear in the Rich Text or Rich Label field. Not used in the Web Link VendorPanel Control
LET $richLabelText := $emailTo;
//↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑

// _______________________________________________________________________
// NO NEED TO CHANGE ANYTHING BETWEEN THESE LINES
LET $HtmlMarkup := "mailto:" & $emailTo & 
IF(ISBLANK($CC), "", "&CC=" & $CC) & 
IF(ISBLANK($BCC), "", "&BCC=" & $BCC) &
IF(ISBLANK($emailSubject), "", "&subject=" & EBMS_URL_ENCODE($emailSubject)) &
IF(ISBLANK($emailBody), "", "&body=" & EBMS_URL_ENCODE($emailBody));

// Replace first '&' with a '?' so that the URL will work
//LET $HtmlMarkup := REGEX_REPLACE($HtmlMarkup, "/&/U",$(match) "____");
LET $arr := SPLIT($HtmlMarkup, "&");
LET $finalURL := ARRAY_INDEX($arr, 1) & "?" & ARRAY_JOIN(ARRAY_SKIP($arr, 1), "&");
// _______________________________________________________________________

RETURN
