FieldEntry.Formula:
LET $data := 
ARRAY_CONCAT(
	[Notes],
	ARRAY(
		ARRAY(
			[Who], 
			NOW(),
			[New Message], 
			[Attachment Names]
		)
	)
);

RETURN 
ARRAY_WHERE(
	$data, $(i,item) 
	AND(
		$item <> ARRAY(),
		$item <> NULL()
	)
)
